import React, { useEffect, useState } from 'react';
import axios from 'axios';

const App = () => {
    const [stocks, setStocks] = useState([]);

    useEffect(() => {
        axios.get('http://127.0.0.1:8000/data') // FastAPI에서 데이터 가져오기
            .then(response => {
                setStocks(response.data.stocks); // 전체 데이터를 상태로 저장
            })
            .catch(error => {
                console.error("데이터를 가져오는 중 오류 발생:", error);
            });
    }, []);

    return (
        <div style={{ margin: '20px' }}>
            <h1>Stock Data</h1>
            <table border="1">
                <thead>
                    <tr>
                        {stocks.length > 0 && Object.keys(stocks[0]).map((key, index) => (
                            <th key={index}>{key}</th> // 데이터의 키를 헤더로 출력
                        ))}
                    </tr>
                </thead>
                <tbody>
                    {stocks.map((stock, index) => (
                        <tr key={index}>
                            {Object.values(stock).map((value, idx) => (
                                <td key={idx}>{value}</td> // 각 데이터를 테이블 셀로 출력
                            ))}
                        </tr>
                    ))}
                </tbody>
            </table>
        </div>
    );
};

export default App;
