import React from "react";
import "../styles/Common.css";
import "../styles/StockTable.css";

const StockTable = ({ stocks }) => {
    if (stocks.length === 0) {
        return <p className="error-message">표시할 데이터가 없습니다.</p>;
    }

    const years = ["2021", "2022", "2023", "2024"];
    const financialKeys = [
        "매출액",
        "영업이익",
        "영업이익률",
        "순이익률",
        "EPS",
        "PER",
        "PBR",
        "주당배당금",
        "시가배당률",
    ];

    const cleanValue = (value) => {
        if (!value || value === "-" || value === "N/A" || value === "") return "N/A";
        return value.toString().replace(/,/g, "");
    };

    const transformedData = years.map((year) => {
        const yearData = { year };

        financialKeys.forEach((key) => {
            const columnName = `${year}.12 ${key}`;
            yearData[key] = columnName in stocks[0] ? cleanValue(stocks[0][columnName]) : "N/A";
        });

        return yearData;
    });

    console.log("Transformed Data:", transformedData);

    return (
        <div className="table-container">
            <table>
                <thead>
                    <tr>
                        <th>연도</th>
                        {financialKeys.map((key, index) => (
                            <th key={index}>{key}</th>
                        ))}
                    </tr>
                </thead>
                <tbody>
                    {transformedData.map((row, index) => (
                        <tr key={index}>
                            <td>{row.year}</td>
                            {financialKeys.map((key, idx) => (
                                <td key={idx}>{row[key]}</td>
                            ))}
                        </tr>
                    ))}
                </tbody>
            </table>
        </div>
    );
};
