import React, { useState, useEffect, useRef } from "react";
import axios from "axios";

const App = () => {
    const [stocks, setStocks] = useState([]);
    const [query, setQuery] = useState("");
    const [filteredStocks, setFilteredStocks] = useState([]);
    const [error, setError] = useState("");
    const [isDropdownOpen, setIsDropdownOpen] = useState(false);
    const dropdownRef = useRef(null);

    const fetchTopStocksByMarketCap = () => {
        axios.get("http://127.0.0.1:8000/top-marketcap")
            .then((response) => {
                if (response.data.error || response.data.stocks.length === 0) {
                    setError("시가총액 데이터가 없습니다.");
                    setStocks([]);
                } else {
                    setStocks(response.data.stocks);
                    setError("");
                }
            })
            .catch((error) => {
                console.error("데이터 로드 중 오류 발생:", error);
                setError("서버와 연결할 수 없습니다.");
                setStocks([]);
            });
    };

    return (
        <div
            style={{
                display: "flex",
                flexDirection: "column",
                justifyContent: "flex-start",
                alignItems: "center",
                height: "100vh",
                paddingTop: "50px",
                fontFamily: "Arial, sans-serif",
            }}
        >
            <h1
                style={{
                    cursor: "pointer",
                    color: "#007bff",
                    textAlign: "center",
                }}
            >
                Stockter
            </h1>
            <div
                style={{
                    display: "grid",
                    gridTemplateColumns: "repeat(auto-fit, minmax(150px, 1fr))",
                    gap: "10px",
                    width: "80%",
                    marginBottom: "20px",
                }}
            >
                {["시가총액", "매출액", "영업이익률", "순이익률", "EPS", "PER", "PBR", "시가배당률(%)"].map((metric) => (
                    <button
                        key={metric}
                        onClick={() => fetchTopStocksByMarketCap(metric)}
                        style={{
                            padding: "10px",
                            fontSize: "14px",
                            background: "#f0f0f0",
                            border: "1px solid #ccc",
                            borderRadius: "4px",
                            cursor: "pointer",
                        }}
                    >
                        {metric}
                    </button>
                ))}
            </div>
            {stocks.length > 0 && (
                <div
                    style={{
                        overflowX: "auto",
                        marginTop: "20px",
                        textAlign: "center",
                        width: "100%",
                    }}
                >
                    <table
                        border="1"
                        style={{
                            width: "max-content",
                            margin: "0 auto",
                            borderCollapse: "collapse",
                        }}
                    >
                        <thead>
                            <tr>
                                {Object.keys(stocks[0]).map((key, index) => (
                                    <th
                                        key={index}
                                        style={{
                                            padding: "8px",
                                            background: "#f2f2f2",
                                            textAlign: "center",
                                        }}
                                    >
                                        {key}
                                    </th>
                                ))}
                            </tr>
                        </thead>
                        <tbody>
                            {stocks.map((stock, index) => (
                                <tr key={index}>
                                    {Object.values(stock).map((value, idx) => (
                                        <td
                                            key={idx}
                                            style={{
                                                padding: "8px",
                                                textAlign: "center",
                                            }}
                                        >
                                            {value}
                                        </td>
                                    ))}
                                </tr>
                            ))}
                        </tbody>
                    </table>
                </div>
            )}
        </div>
    );
};

export default App;
