import React, { useState } from "react";
import axios from "axios";

const App = () => {
    const [stocks, setStocks] = useState([]);
    const [query, setQuery] = useState("");
    const [error, setError] = useState("");

    const fetchData = () => {
        if (!query.trim()) {
            setError("검색어를 입력하세요.");
            return;
        }
        setError(""); // 이전 오류 초기화

        // FastAPI로 검색어 전송 (종목코드 또는 종목명)
        axios.get("http://127.0.0.1:8000/data", { params: { query } })
            .then((response) => {
                if (response.data.error) {
                    setError(response.data.error);
                    setStocks([]); // 오류 시 테이블 초기화
                } else if (response.data.stocks.length === 0) {
                    setError("검색 결과가 없습니다.");
                    setStocks([]);
                } else {
                    setStocks(response.data.stocks); // 검색된 종목만 저장
                    setError("");
                }
            })
            .catch((error) => {
                console.error("데이터 로드 중 오류 발생:", error);
                setError("서버와 연결할 수 없습니다.");
                setStocks([]);
            });
    };

    return (
        <div style={{ margin: "20px" }}>
            <h1>Stock Search</h1>
            <div style={{ marginBottom: "10px", display: "flex", alignItems: "center" }}>
                <input
                    type="text"
                    placeholder="종목명 또는 종목코드 입력"
                    value={query}
                    onChange={(e) => setQuery(e.target.value)}
                    style={{
                        flex: 1,
                        padding: "10px",
                        fontSize: "16px",
                        border: "1px solid #ccc",
                        borderRadius: "4px 0 0 4px",
                    }}
                />
                <button
                    onClick={fetchData}
                    style={{
                        padding: "10px 20px",
                        fontSize: "16px",
                        background: "#007bff",
                        color: "#fff",
                        border: "none",
                        borderRadius: "0 4px 4px 0",
                        cursor: "pointer",
                    }}
                >
                    검색
                </button>
            </div>
            {error && <p style={{ color: "red" }}>{error}</p>}
            {stocks.length > 0 && (
                <table border="1" style={{ width: "100%", borderCollapse: "collapse", marginTop: "20px" }}>
                    <thead>
                        <tr>
                            {Object.keys(stocks[0]).map((key, index) => (
                                <th key={index} style={{ padding: "8px", background: "#f2f2f2" }}>{key}</th>
                            ))}
                        </tr>
                    </thead>
                    <tbody>
                        {stocks.map((stock, index) => (
                            <tr key={index}>
                                {Object.values(stock).map((value, idx) => (
                                    <td key={idx} style={{ padding: "8px", textAlign: "center" }}>
                                        {value}
                                    </td>
                                ))}
                            </tr>
                        ))}
                    </tbody>
                </table>
            )}
        </div>
    );
};

export default App;
