import React, { useState, useEffect, useRef } from "react";
import axios from "axios";

const App = () => {
    const [stocks, setStocks] = useState([]);
    const [query, setQuery] = useState("");
    const [filteredStocks, setFilteredStocks] = useState([]);
    const [error, setError] = useState("");
    const [isDropdownOpen, setIsDropdownOpen] = useState(false);
    const dropdownRef = useRef(null);

    // 추가: 매출액 연도 선택 상태
    const [revenueYear, setRevenueYear] = useState("2024");

    // 검색어 강조 표시 함수
    const highlightMatch = (text, query) => {
        if (!query) return text;
        const regex = new RegExp(`(${query})`, "gi");
        return text.replace(regex, (match) => `<span style="color: red">${match}</span>`);
    };

    // 검색 데이터 가져오기
    const fetchData = (searchQuery = query) => {
        if (!searchQuery.trim()) {
            setError("검색어를 입력하세요.");
            return;
        }
        setError("");
        setFilteredStocks([]);
        setIsDropdownOpen(false);

        axios.get("http://127.0.0.1:8000/data", { params: { query: searchQuery } })
            .then((response) => {
                if (response.data.error || response.data.stocks.length === 0) {
                    setError("검색 결과가 없습니다.");
                    setStocks([]);
                } else {
                    setStocks(response.data.stocks);
                    setError("");
                }
            })
            .catch((error) => {
                console.error("데이터 로드 중 오류 발생:", error);
                setError("서버와 연결할 수 없습니다.");
                setStocks([]);
            });
    };

    // 시가총액 상위 50개 가져오기
    const fetchTopStocksByMarketCap = () => {
        axios.get("http://127.0.0.1:8000/top-marketcap")
            .then((response) => {
                if (response.data.error || response.data.stocks.length === 0) {
                    setError("시가총액 데이터가 없습니다.");
                    setStocks([]);
                } else {
                    setStocks(response.data.stocks);
                    setError("");
                }
            })
            .catch((error) => {
                console.error("데이터 로드 중 오류 발생:", error);
                setError("서버와 연결할 수 없습니다.");
                setStocks([]);
            });
    };

    // 매출액 상위 50개 가져오기 (수정: 연도 파라미터 추가)
    const fetchTopStocksByRevenue = (year) => {
        axios.get("http://127.0.0.1:8000/top-revenue", { params: { year } })
            .then((response) => {
                if (response.data.error || response.data.stocks.length === 0) {
                    setError("매출액 데이터가 없습니다.");
                    setStocks([]);
                } else {
                    setStocks(response.data.stocks);
                    setError("");
                }
            })
            .catch((error) => {
                console.error("데이터 로드 중 오류 발생:", error);
                setError("서버와 연결할 수 없습니다.");
                setStocks([]);
            });
    };

    // 입력값 변경
    const handleQueryChange = (e) => {
        const input = e.target.value;
        setQuery(input);

        if (input.trim() === "") {
            setFilteredStocks([]);
            setIsDropdownOpen(false);
        } else {
            axios.get("http://127.0.0.1:8000/data", { params: { query: input } })
                .then((response) => {
                    if (response.data.stocks) {
                        const lowercasedInput = input.toLowerCase();
                        const filtered = response.data.stocks.filter((stock) => {
                            const stockCode = stock["종목코드"].toString().toLowerCase();
                            const stockName = stock["종목명"].toLowerCase();
                            return stockCode.includes(lowercasedInput) || stockName.includes(lowercasedInput);
                        });

                        const startsWithQuery = filtered.filter((stock) => {
                            const stockCode = stock["종목코드"].toString().toLowerCase();
                            const stockName = stock["종목명"].toLowerCase();
                            return stockCode.startsWith(lowercasedInput) || stockName.startsWith(lowercasedInput);
                        });

                        const others = filtered.filter((stock) => {
                            const stockCode = stock["종목코드"].toString().toLowerCase();
                            const stockName = stock["종목명"].toLowerCase();
                            return !stockCode.startsWith(lowercasedInput) && !stockName.startsWith(lowercasedInput);
                        });

                        setFilteredStocks([...startsWithQuery, ...others].slice(0, 10));
                        setIsDropdownOpen(true);
                    }
                })
                .catch((error) => {
                    console.error("자동완성 데이터 로드 오류:", error);
                });
        }
    };

    // 초기 상태로 리셋
    const resetState = () => {
        setQuery("");
        setStocks([]);
        setFilteredStocks([]);
        setError("");
        setIsDropdownOpen(false);
    };

    // 추천 클릭
    const handleSuggestionClick = (stock) => {
        setQuery(stock["종목명"]);
        setIsDropdownOpen(false);
        fetchData(stock["종목명"]);
    };

    // 엔터 키 동작
    const handleKeyPress = (e) => {
        if (e.key === "Enter") {
            fetchData();
        }
    };

    // 버튼 클릭 동작 (수정: '매출액' 버튼 처리 추가)
    const handleButtonClick = (metric) => {
        if (metric === "시가총액") {
            fetchTopStocksByMarketCap();
        } else if (metric === "매출액") {
            fetchTopStocksByRevenue(revenueYear);
        } else {
            // 다른 메트릭에 대한 처리 (필요 시 추가)
            // 예: axios.get(`http://127.0.0.1:8000/top-${metric.toLowerCase()}`)
            setError(`"${metric}" 기능은 아직 구현되지 않았습니다.`);
            setStocks([]);
        }
    };

    // 외부 클릭 이벤트 감지
    useEffect(() => {
        const handleOutsideClick = (e) => {
            if (dropdownRef.current && !dropdownRef.current.contains(e.target)) {
                setIsDropdownOpen(false);
            }
        };
        document.addEventListener("mousedown", handleOutsideClick);
        return () => {
            document.removeEventListener("mousedown", handleOutsideClick);
        };
    }, []);

    return (
        <div
            style={{
                display: "flex",
                flexDirection: "column",
                justifyContent: "flex-start",
                alignItems: "center",
                height: "100vh",
                paddingTop: "50px",
                fontFamily: "Arial, sans-serif",
            }}
        >
            <h1
                onClick={resetState}
                style={{
                    cursor: "pointer",
                    color: "#007bff",
                    textAlign: "center",
                }}
            >
                Stockter
            </h1>
            <div
                style={{
                    marginBottom: isDropdownOpen ? `${Math.min(filteredStocks.length * 40, 200) + 40}px` : "20px",
                    position: "relative",
                    display: "flex",
                    alignItems: "center",
                    justifyContent: "center",
                    width: "600px",
                }}
            >
                <input
                    type="text"
                    placeholder="종목명 또는 종목코드 입력"
                    value={query}
                    onChange={handleQueryChange}
                    onKeyPress={handleKeyPress}
                    style={{
                        flex: 1,
                        padding: "10px",
                        fontSize: "16px",
                        border: "1px solid #ccc",
                        borderRadius: "4px 0 0 4px",
                    }}
                />
                <button
                    onClick={fetchData}
                    style={{
                        padding: "10px 20px",
                        fontSize: "16px",
                        background: "#007bff",
                        color: "#fff",
                        border: "none",
                        borderRadius: "0 4px 4px 0",
                        cursor: "pointer",
                    }}
                >
                    종목 검색
                </button>
                {isDropdownOpen && (
                    <div
                        ref={dropdownRef}
                        style={{
                            position: "absolute",
                            top: "50px",
                            width: "100%",
                            maxWidth: "600px",
                            background: "#fff",
                            border: "1px solid #ccc",
                            zIndex: 1000,
                            maxHeight: "200px",
                            overflowY: "auto",
                        }}
                    >
                        {filteredStocks.map((stock, index) => (
                            <div
                                key={index}
                                onMouseEnter={(e) => (e.currentTarget.style.backgroundColor = "#e6f7ff")}
                                onMouseLeave={(e) => (e.currentTarget.style.backgroundColor = "white")}
                                onClick={() => handleSuggestionClick(stock)}
                                style={{
                                    padding: "8px",
                                    cursor: "pointer",
                                    borderBottom: "1px solid #eee",
                                    transition: "background-color 0.2s ease",
                                    display: "flex",
                                    justifyContent: "space-between",
                                }}
                            >
                                <span
                                    dangerouslySetInnerHTML={{
                                        __html: highlightMatch(stock["종목명"], query),
                                    }}
                                ></span>
                                <span
                                    dangerouslySetInnerHTML={{
                                        __html: highlightMatch(stock["종목코드"].toString(), query),
                                    }}
                                    style={{ color: "#666", fontSize: "14px" }}
                                ></span>
                            </div>
                        ))}
                    </div>
                )}
            </div>
            {error && <p style={{ color: "red" }}>{error}</p>}
            <div
                style={{
                    display: "flex",
                    flexWrap: "wrap",
                    gap: "10px",
                    width: "80%",
                    marginBottom: "20px",
                    justifyContent: "center",
                }}
            >
                {/* 다른 메트릭 버튼 */}
                {["시가총액", "영업이익률", "순이익률", "EPS", "PER", "PBR", "시가배당률(%)"].map((metric) => (
                    <button
                        key={metric}
                        onClick={() => handleButtonClick(metric)}
                        style={{
                            padding: "10px",
                            fontSize: "14px",
                            background: "#f0f0f0",
                            border: "1px solid #ccc",
                            borderRadius: "4px",
                            cursor: "pointer",
                        }}
                    >
                        {metric}
                    </button>
                ))}

                {/* 매출액 버튼과 연도 선택 드롭다운 */}
                <div style={{ display: "flex", alignItems: "center", gap: "5px" }}>
                    <button
                        onClick={() => handleButtonClick("매출액")}
                        style={{
                            padding: "10px",
                            fontSize: "14px",
                            background: "#f0f0f0",
                            border: "1px solid #ccc",
                            borderRadius: "4px",
                            cursor: "pointer",
                        }}
                    >
                        매출액
                    </button>
                    <select
                        value={revenueYear}
                        onChange={(e) => setRevenueYear(e.target.value)}
                        style={{
                            padding: "8px",
                            fontSize: "14px",
                            border: "1px solid #ccc",
                            borderRadius: "4px",
                            cursor: "pointer",
                        }}
                    >
                        <option value="2021">2021</option>
                        <option value="2022">2022</option>
                        <option value="2023">2023</option>
                        <option value="2024">2024</option>
                    </select>
                </div>
            </div>
            {stocks.length > 0 && (
                <div
                    style={{
                        overflowX: "auto",
                        marginTop: "20px",
                        textAlign: "left",
                        width: "100%",
                        display: "flex",
                        justifyContent: "center",  // 가운데 정렬
                    }}
                >
                    <table
                        border="1"
                        style={{
                            width: "max-content",
                            borderCollapse: "collapse",
                        }}
                    >
                        <thead>
                            <tr>
                                {Object.keys(stocks[0]).map((key, index) => (
                                    <th
                                        key={index}
                                        style={{
                                            padding: "8px",
                                            background: "#f2f2f2",
                                            textAlign: "center",
                                        }}
                                    >
                                        {key}
                                    </th>
                                ))}
                            </tr>
                        </thead>
                        <tbody>
                            {stocks.map((stock, index) => (
                                <tr key={index}>
                                    {Object.values(stock).map((value, idx) => (
                                        <td
                                            key={idx}
                                            style={{
                                                padding: "8px",
                                                textAlign: "center",
                                            }}
                                        >
                                            {value}
                                        </td>
                                    ))}
                                </tr>
                            ))}
                        </tbody>
                    </table>
                </div>
            )}
        </div>
    );
};

export default App;
