import React, { useState } from "react";
import axios from "axios";

const App = () => {
    const [stocks, setStocks] = useState([]);
    const [query, setQuery] = useState("");
    const [filteredStocks, setFilteredStocks] = useState([]);
    const [error, setError] = useState("");

    const fetchData = (searchQuery) => {
        const queryToSearch = searchQuery || query;
        if (!queryToSearch.trim()) {
            setError("검색어를 입력하세요.");
            return;
        }
        setError("");
        setFilteredStocks([]);

        axios.get("http://127.0.0.1:8000/data", { params: { query: queryToSearch } })
            .then((response) => {
                if (response.data.error) {
                    setError(response.data.error);
                    setStocks([]);
                } else if (response.data.stocks.length === 0) {
                    setError("검색 결과가 없습니다.");
                    setStocks([]);
                } else {
                    setStocks(response.data.stocks);
                    setError("");
                }
            })
            .catch((error) => {
                console.error("데이터 로드 중 오류 발생:", error);
                setError("서버와 연결할 수 없습니다.");
                setStocks([]);
            });
    };

    const handleQueryChange = (e) => {
        const input = e.target.value;
        setQuery(input);

        if (input.trim() === "") {
            setFilteredStocks([]);
        } else {
            axios.get("http://127.0.0.1:8000/data", { params: { query: input } })
                .then((response) => {
                    if (response.data.stocks) {
                        const lowercasedInput = input.toLowerCase();
                        const matches = response.data.stocks.filter((stock) => {
                            const stockCode = stock["종목코드"].toString().toLowerCase();
                            const stockName = stock["종목명"].toLowerCase();
                            return (
                                stockCode.includes(lowercasedInput) ||
                                stockName.includes(lowercasedInput)
                            );
                        });

                        const startsWithQuery = matches.filter((stock) => {
                            const stockCode = stock["종목코드"].toString().toLowerCase();
                            const stockName = stock["종목명"].toLowerCase();
                            return (
                                stockCode.startsWith(lowercasedInput) ||
                                stockName.startsWith(lowercasedInput)
                            );
                        });

                        const others = matches.filter((stock) => {
                            const stockCode = stock["종목코드"].toString().toLowerCase();
                            const stockName = stock["종목명"].toLowerCase();
                            return (
                                !stockCode.startsWith(lowercasedInput) &&
                                !stockName.startsWith(lowercasedInput)
                            );
                        });

                        setFilteredStocks([...startsWithQuery, ...others].slice(0, 10));
                    }
                })
                .catch((error) => {
                    console.error("자동완성 데이터 로드 오류:", error);
                });
        }
    };

    return (
        <div style={{ margin: "20px", fontFamily: "Arial, sans-serif" }}>
            <h1>Stock Search</h1>
            <div
                style={{
                    marginBottom: "10px",
                    position: "relative",
                    display: "flex",
                    flexDirection: "column",
                    alignItems: "center",
                }}
            >
                <input
                    type="text"
                    placeholder="종목명 또는 종목코드 입력"
                    value={query}
                    onChange={handleQueryChange}
                    style={{
                        width: "100%",
                        maxWidth: "600px",
                        padding: "10px",
                        fontSize: "16px",
                        border: "1px solid #ccc",
                        borderRadius: "4px",
                        marginBottom: "10px",
                    }}
                />
                <button
                    onClick={() => fetchData()}
                    style={{
                        width: "100%",
                        maxWidth: "200px",
                        padding: "10px",
                        fontSize: "16px",
                        background: "#007bff",
                        color: "#fff",
                        border: "none",
                        borderRadius: "4px",
                        cursor: "pointer",
                    }}
                >
                    검색
                </button>
                {filteredStocks.length > 0 && (
                    <div
                        style={{
                            position: "absolute",
                            top: "60px",
                            width: "100%",
                            maxWidth: "600px",
                            background: "#fff",
                            border: "1px solid #ccc",
                            zIndex: 1000,
                            maxHeight: "200px",
                            overflowY: "auto",
                        }}
                    >
                        {filteredStocks.map((stock, index) => (
                            <div
                                key={index}
                                onMouseEnter={(e) => (e.currentTarget.style.backgroundColor = "#e6f7ff")}
                                onMouseLeave={(e) => (e.currentTarget.style.backgroundColor = "white")}
                                onClick={() => {
                                    fetchData(stock["종목명"]);
                                    setFilteredStocks([]);
                                }}
                                style={{
                                    padding: "8px",
                                    cursor: "pointer",
                                    borderBottom: "1px solid #eee",
                                    transition: "background-color 0.2s ease",
                                }}
                            >
                                {stock["종목명"]} ({stock["종목코드"]})
                            </div>
                        ))}
                    </div>
                )}
            </div>
            {error && <p style={{ color: "red" }}>{error}</p>}
            {stocks.length > 0 && (
                <div style={{ overflowX: "auto" }}>
                    <table
                        border="1"
                        style={{
                            width: "100%",
                            maxWidth: "100%",
                            borderCollapse: "collapse",
                            marginTop: "20px",
                        }}
                    >
                        <thead>
                            <tr>
                                {Object.keys(stocks[0]).map((key, index) => (
                                    <th key={index} style={{ padding: "8px", background: "#f2f2f2" }}>{key}</th>
                                ))}
                            </tr>
                        </thead>
                        <tbody>
                            {stocks.map((stock, index) => (
                                <tr key={index}>
                                    {Object.values(stock).map((value, idx) => (
                                        <td key={idx} style={{ padding: "8px", textAlign: "center" }}>
                                            {value}
                                        </td>
                                    ))}
                                </tr>
                            ))}
                        </tbody>
                    </table>
                </div>
            )}
        </div>
    );
};

export default App;
