import React, { useState } from "react";
import axios from "axios";

const App = () => {
    const [stocks, setStocks] = useState([]);
    const [query, setQuery] = useState("");
    const [filteredStocks, setFilteredStocks] = useState([]);
    const [error, setError] = useState("");

    const fetchData = (searchQuery) => {
        const queryToSearch = searchQuery || query; // 클릭한 종목이 있다면 해당 값 사용
        if (!queryToSearch.trim()) {
            setError("검색어를 입력하세요.");
            return;
        }
        setError(""); // 이전 오류 초기화

        // FastAPI로 검색어 전송 (종목코드 또는 종목명)
        axios.get("http://127.0.0.1:8000/data", { params: { query: queryToSearch } })
            .then((response) => {
                if (response.data.error) {
                    setError(response.data.error);
                    setStocks([]); // 오류 시 테이블 초기화
                } else if (response.data.stocks.length === 0) {
                    setError("검색 결과가 없습니다.");
                    setStocks([]);
                } else {
                    setStocks(response.data.stocks); // 검색된 종목만 저장
                    setError("");
                }
            })
            .catch((error) => {
                console.error("데이터 로드 중 오류 발생:", error);
                setError("서버와 연결할 수 없습니다.");
                setStocks([]);
            });
    };

    const handleQueryChange = (e) => {
        const input = e.target.value;
        setQuery(input);

        // 실시간으로 필터링된 목록 생성
        if (input.trim() === "") {
            setFilteredStocks([]);
        } else {
            axios.get("http://127.0.0.1:8000/data", { params: { query: input } })
                .then((response) => {
                    if (response.data.stocks) {
                        const lowercasedInput = input.toLowerCase(); // 소문자로 변환
                        const matches = response.data.stocks.filter((stock) => {
                            const stockCode = stock["종목코드"].toString().toLowerCase();
                            const stockName = stock["종목명"].toLowerCase();
                            return (
                                stockCode.includes(lowercasedInput) || // 종목코드 포함
                                stockName.includes(lowercasedInput) // 종목명 포함
                            );
                        });

                        // `query`로 시작하는 항목을 우선 표시
                        const startsWithQuery = matches.filter((stock) => {
                            const stockCode = stock["종목코드"].toString().toLowerCase();
                            const stockName = stock["종목명"].toLowerCase();
                            return (
                                stockCode.startsWith(lowercasedInput) || // 종목코드로 시작
                                stockName.startsWith(lowercasedInput) // 종목명으로 시작
                            );
                        });

                        const others = matches.filter((stock) => {
                            const stockCode = stock["종목코드"].toString().toLowerCase();
                            const stockName = stock["종목명"].toLowerCase();
                            return (
                                !stockCode.startsWith(lowercasedInput) &&
                                !stockName.startsWith(lowercasedInput)
                            );
                        });

                        setFilteredStocks([...startsWithQuery, ...others].slice(0, 10)); // 최대 10개 표시
                    }
                })
                .catch((error) => {
                    console.error("자동완성 데이터 로드 오류:", error);
                });
        }
    };

    return (
        <div style={{ margin: "20px" }}>
            <h1>Stock Search</h1>
            <div style={{ marginBottom: "10px", position: "relative", display: "flex", alignItems: "center" }}>
                <input
                    type="text"
                    placeholder="종목명 또는 종목코드 입력"
                    value={query}
                    onChange={handleQueryChange}
                    style={{
                        flex: 1,
                        padding: "10px",
                        fontSize: "16px",
                        border: "1px solid #ccc",
                        borderRadius: "4px 0 0 4px",
                    }}
                />
                <button
                    onClick={() => fetchData()}
                    style={{
                        padding: "10px 20px",
                        fontSize: "16px",
                        background: "#007bff",
                        color: "#fff",
                        border: "none",
                        borderRadius: "0 4px 4px 0",
                        cursor: "pointer",
                    }}
                >
                    검색
                </button>
                {filteredStocks.length > 0 && (
                    <div
                        style={{
                            position: "absolute",
                            top: "40px",
                            left: "0",
                            right: "0",
                            background: "#fff",
                            border: "1px solid #ccc",
                            zIndex: 1000,
                            maxHeight: "200px",
                            overflowY: "auto",
                        }}
                    >
                        {filteredStocks.map((stock, index) => (
                            <div
                                key={index}
                                onMouseEnter={(e) => e.currentTarget.style.backgroundColor = "#e6f7ff"} // 마우스 오버
                                onMouseLeave={(e) => e.currentTarget.style.backgroundColor = "white"} // 마우스 아웃
                                onClick={() => {
                                    fetchData(stock["종목명"]); // 클릭 시 해당 종목 검색
                                    setFilteredStocks([]); // 추천 목록 닫기
                                }}
                                style={{
                                    padding: "8px",
                                    cursor: "pointer",
                                    borderBottom: "1px solid #eee",
                                    transition: "background-color 0.2s ease", // 부드러운 전환 효과
                                }}
                            >
                                {stock["종목명"]} ({stock["종목코드"]})
                            </div>
                        ))}
                    </div>
                )}
            </div>
            {error && <p style={{ color: "red" }}>{error}</p>}
            {stocks.length > 0 && (
                <table border="1" style={{ width: "100%", borderCollapse: "collapse", marginTop: "20px" }}>
                    <thead>
                        <tr style={{ backgroundColor: "#f2f2f2", textAlign: "center" }}>
                            <th style={{ padding: "10px" }}>종목명</th>
                            <th style={{ padding: "10px" }}>종목코드</th>
                            <th style={{ padding: "10px" }}>시가총액</th>
                            <th style={{ padding: "10px" }}>현재가</th>
                        </tr>
                    </thead>
                    <tbody>
                        {stocks.map((stock, index) => (
                            <tr key={index} style={{ textAlign: "center" }}>
                                <td style={{ padding: "10px", fontWeight: "bold" }}>{stock["종목명"]}</td>
                                <td style={{ padding: "10px" }}>{stock["종목코드"]}</td>
                                <td style={{ padding: "10px", color: "green" }}>{stock["시가총액"]}억</td>
                                <td style={{ padding: "10px", color: "blue" }}>{stock["현재가"]}원</td>
                            </tr>
                        ))}
                    </tbody>
                </table>
            )}
        </div>
    );
};

export default App;
