import React, { useState, useEffect } from "react";
import axios from "axios";
import SimpleTable from "./SimpleTable";
import "../styles/Common.css";
import "../styles/FinancialStatementsPage.css";

const FinancialStatementsPage = () => {
    const [stocks, setStocks] = useState([]);
    const [error, setError] = useState("");
    const [selectedYear, setSelectedYear] = useState("2024");
    const [currentMetric, setCurrentMetric] = useState(null);

    // 데이터 가져오기
    const fetchTopStocksByMetric = () => {
        if (currentMetric === "시가총액") {
            axios
                .get("http://127.0.0.1:8000/top-marketcap")
                .then((response) => {
                    if (response.data.error || response.data.stocks.length === 0) {
                        setError("시가총액 데이터가 없습니다.");
                        setStocks([]);
                    } else {
                        setStocks(response.data.stocks);
                        setError("");
                    }
                })
                .catch((error) => {
                    console.error("데이터 로드 중 오류 발생:", error);
                    setError("서버와 연결할 수 없습니다.");
                    setStocks([]);
                });
        } else if (currentMetric === "매출액") {
            axios
                .get("http://127.0.0.1:8000/top-revenue", { params: { year: selectedYear } })
                .then((response) => {
                    if (response.data.error || response.data.stocks.length === 0) {
                        setError("매출액 데이터가 없습니다.");
                        setStocks([]);
                    } else {
                        setStocks(response.data.stocks);
                        setError("");
                    }
                })
                .catch((error) => {
                    console.error("데이터 로드 중 오류 발생:", error);
                    setError("서버와 연결할 수 없습니다.");
                    setStocks([]);
                });
        } else {
            setError(`"${currentMetric}" 기능은 아직 구현되지 않았습니다.`);
            setStocks([]);
        }
    };

    // 버튼 클릭 동작
    const handleButtonClick = (metric) => {
        setCurrentMetric(metric);
        if (metric === "시가총액") {
            setSelectedYear(null); // 연도 선택 비활성화
        } else if (metric === "매출액") {
            setSelectedYear("2024"); // 기본 연도 선택
        }
    };

    // 연도 버튼 클릭 동작
    const handleYearButtonClick = (year) => {
        if (currentMetric === "매출액") {
            setSelectedYear(year);
        }
    };

    // 연도 변경 시 현재 선택된 메트릭에 따라 데이터 재요청
    useEffect(() => {
        if (currentMetric) {
            fetchTopStocksByMetric();
        }
    }, [selectedYear, currentMetric]);

    return (
        <div className="financial-statements-container">
            <h2 style={{ color: "#007bff", marginBottom: "20px" }}>재무제표</h2>

            {/* 정렬기준과 버튼들 */}
            <div style={{ display: "flex", flexDirection: "column", gap: "10px", marginBottom: "20px" }}>
                <div style={{ fontSize: "14px", fontWeight: "bold", marginBottom: "5px" }}>
                    정렬기준:
                </div>
                <div style={{ display: "flex", alignItems: "center", gap: "10px" }}>
                    {/* 시가총액 버튼 */}
                    <button
                        onClick={() => handleButtonClick("시가총액")}
                        style={{
                            backgroundColor: currentMetric === "시가총액" ? "#007bff" : "#fff",
                            color: currentMetric === "시가총액" ? "#fff" : "#000",
                            border: "1px solid #007bff",
                            borderRadius: "4px",
                            padding: "5px 10px",
                        }}
                    >
                        시가총액
                    </button>

                    {/* 연도 버튼들 */}
                    <div style={{ display: "flex", gap: "5px" }}>
                        {["2021", "2022", "2023", "2024"].map((year) => (
                            <button
                                key={year}
                                onClick={() => handleYearButtonClick(year)}
                                style={{
                                    backgroundColor: selectedYear === year && currentMetric === "매출액" ? "#007bff" : "#fff",
                                    color: selectedYear === year && currentMetric === "매출액" ? "#fff" : "#000",
                                    border: "1px solid #007bff",
                                    borderRadius: "4px",
                                    padding: "5px 10px",
                                    cursor: currentMetric === "매출액" ? "pointer" : "not-allowed",
                                    opacity: currentMetric === "매출액" ? "1" : "0.5",
                                }}
                                disabled={currentMetric !== "매출액"}
                            >
                                {year}
                            </button>
                        ))}
                    </div>

                    {/* 매출액 버튼 */}
                    <button
                        onClick={() => handleButtonClick("매출액")}
                        style={{
                            backgroundColor: currentMetric === "매출액" ? "#007bff" : "#fff",
                            color: currentMetric === "매출액" ? "#fff" : "#000",
                            border: "1px solid #007bff",
                            borderRadius: "4px",
                            padding: "5px 10px",
                        }}
                    >
                        매출액
                    </button>
                </div>
            </div>

            {error && <p className="error-message">{error}</p>}

            {/* 데이터 테이블 */}
            {stocks.length > 0 && (
                <SimpleTable
                    stocks={stocks}
                    columnTitle={currentMetric} // 동적으로 제목 변경
                />
            )}
        </div>
    );
};

export default FinancialStatementsPage;
