import React, { useState } from "react";
import axios from "axios";

const App = () => {
    const [stocks, setStocks] = useState([]);
    const [stockCode, setStockCode] = useState("");
    const [stockName, setStockName] = useState("");
    const [error, setError] = useState("");

    const fetchData = () => {
        // FastAPI로 쿼리 전송
        const params = {};
        if (stockCode) params.stock_code = stockCode;
        if (stockName) params.stock_name = stockName;

        axios.get("http://127.0.0.1:8000/data", { params })
            .then((response) => {
                if (response.data.error) {
                    setError(response.data.error);
                } else {
                    setStocks(response.data.stocks);
                    setError("");
                }
            })
            .catch((error) => {
                console.error("데이터 로드 중 오류 발생:", error);
                setError("서버와 연결할 수 없습니다.");
            });
    };

    return (
        <div style={{ margin: "20px" }}>
            <h1>Stock Search</h1>
            <div style={{ marginBottom: "10px" }}>
                <input
                    type="text"
                    placeholder="종목 코드 입력"
                    value={stockCode}
                    onChange={(e) => setStockCode(e.target.value)}
                />
                <input
                    type="text"
                    placeholder="종목명 입력"
                    value={stockName}
                    onChange={(e) => setStockName(e.target.value)}
                />
                <button onClick={fetchData}>검색</button>
            </div>
            {error ? (
                <p style={{ color: "red" }}>{error}</p>
            ) : (
                stocks.length > 0 ? (
                    <table border="1" style={{ width: "100%", borderCollapse: "collapse" }}>
                        <thead>
                            <tr>
                                {Object.keys(stocks[0]).map((key, index) => (
                                    <th key={index} style={{ padding: "8px", background: "#f2f2f2" }}>{key}</th>
                                ))}
                            </tr>
                        </thead>
                        <tbody>
                            {stocks.map((stock, index) => (
                                <tr key={index}>
                                    {Object.values(stock).map((value, idx) => (
                                        <td key={idx} style={{ padding: "8px", textAlign: "center" }}>
                                            {value}
                                        </td>
                                    ))}
                                </tr>
                            ))}
                        </tbody>
                    </table>
                ) : (
                    <p>검색 결과가 없습니다.</p>
                )
            )}
        </div>
    );
};

export default App;
