import React, { useState, useEffect, useRef } from "react";
import axios from "axios";

const App = () => {
    const [stocks, setStocks] = useState([]);
    const [query, setQuery] = useState("");
    const [filteredStocks, setFilteredStocks] = useState([]);
    const [error, setError] = useState("");
    const [isDropdownOpen, setIsDropdownOpen] = useState(false);
    const dropdownRef = useRef(null);

    // 연도 선택 상태
    const [selectedYear, setSelectedYear] = useState("2024");
    const [currentMetric, setCurrentMetric] = useState(null);

    // 숫자 포맷팅 함수 (시가총액: 억 단위, 매출액: 조 및 억 단위)
    const formatNumber = (key, number) => {
        if (typeof number !== "number" || isNaN(number)) return number;

        if (key === "시가총액") {
            // 시가총액은 항상 억 단위로 표시
            return (number / 10 ** 8).toFixed(2) + "억";
        } else if (key === "매출액") {
            // 매출액은 10,000 억 이상일 경우 조와 억 단위, 미만일 경우 억 단위
            if (number >= 10 ** 12) {
                const trillion = Math.floor(number / 10 ** 12);
                const remaining = Math.floor((number % 10 ** 12) / 10 ** 8);
                return `${trillion}조 ${remaining}억원`;
            } else if (number >= 10 ** 8) {
                return (number / 10 ** 8).toFixed(2) + "억원";
            } else {
                return number.toString();
            }
        } else {
            return number.toString();
        }
    };

    // 검색어 강조 표시 함수
    const highlightMatch = (text, query) => {
        if (!query) return text;
        const regex = new RegExp(`(${query})`, "gi");
        return text.replace(regex, (match) => `<span style="color: red">${match}</span>`);
    };

    // 검색 데이터 가져오기
    const fetchData = (searchQuery = query) => {
        if (!searchQuery.trim()) {
            setError("검색어를 입력하세요.");
            return;
        }
        setError("");
        setFilteredStocks([]);
        setIsDropdownOpen(false);
        setCurrentMetric(null); // 검색 시 현재 메트릭 초기화

        axios
            .get("http://127.0.0.1:8000/data", { params: { query: searchQuery } })
            .then((response) => {
                if (response.data.error || response.data.stocks.length === 0) {
                    setError("검색 결과가 없습니다.");
                    setStocks([]);
                } else {
                    setStocks(response.data.stocks);
                    setError("");
                }
            })
            .catch((error) => {
                console.error("데이터 로드 중 오류 발생:", error);
                setError("서버와 연결할 수 없습니다.");
                setStocks([]);
            });
    };

    // 시가총액 상위 50개 가져오기
    const fetchTopStocksByMarketCap = () => {
        axios
            .get("http://127.0.0.1:8000/top-marketcap")
            .then((response) => {
                if (response.data.error || response.data.stocks.length === 0) {
                    setError("시가총액 데이터가 없습니다.");
                    setStocks([]);
                } else {
                    setStocks(response.data.stocks);
                    setError("");
                }
            })
            .catch((error) => {
                console.error("데이터 로드 중 오류 발생:", error);
                setError("서버와 연결할 수 없습니다.");
                setStocks([]);
            });
    };

    // 매출액 상위 50개 가져오기 (연도 파라미터 추가)
    const fetchTopStocksByRevenue = (year) => {
        axios
            .get("http://127.0.0.1:8000/top-revenue", { params: { year } })
            .then((response) => {
                if (response.data.error || response.data.stocks.length === 0) {
                    setError("매출액 데이터가 없습니다.");
                    setStocks([]);
                } else {
                    setStocks(response.data.stocks);
                    setError("");
                }
            })
            .catch((error) => {
                console.error("데이터 로드 중 오류 발생:", error);
                setError("서버와 연결할 수 없습니다.");
                setStocks([]);
            });
    };

    // 입력값 변경
    const handleQueryChange = (e) => {
        const input = e.target.value;
        setQuery(input);

        if (input.trim() === "") {
            setFilteredStocks([]);
            setIsDropdownOpen(false);
        } else {
            axios
                .get("http://127.0.0.1:8000/data", { params: { query: input } })
                .then((response) => {
                    if (response.data.stocks) {
                        const lowercasedInput = input.toLowerCase();
                        const filtered = response.data.stocks.filter((stock) => {
                            const stockCode = stock["종목코드"].toString().toLowerCase();
                            const stockName = stock["종목명"].toLowerCase();
                            return stockCode.includes(lowercasedInput) || stockName.includes(lowercasedInput);
                        });

                        const startsWithQuery = filtered.filter((stock) => {
                            const stockCode = stock["종목코드"].toString().toLowerCase();
                            const stockName = stock["종목명"].toLowerCase();
                            return stockCode.startsWith(lowercasedInput) || stockName.startsWith(lowercasedInput);
                        });

                        const others = filtered.filter((stock) => {
                            const stockCode = stock["종목코드"].toString().toLowerCase();
                            const stockName = stock["종목명"].toLowerCase();
                            return !stockCode.startsWith(lowercasedInput) && !stockName.startsWith(lowercasedInput);
                        });

                        setFilteredStocks([...startsWithQuery, ...others].slice(0, 10));
                        setIsDropdownOpen(true);
                    }
                })
                .catch((error) => {
                    console.error("자동완성 데이터 로드 오류:", error);
                });
        }
    };

    // 초기 상태로 리셋
    const resetState = () => {
        setQuery("");
        setStocks([]);
        setFilteredStocks([]);
        setError("");
        setIsDropdownOpen(false);
        setCurrentMetric(null);
    };

    // 추천 클릭
    const handleSuggestionClick = (stock) => {
        setQuery(stock["종목명"]);
        setIsDropdownOpen(false);
        fetchData(stock["종목명"]);
    };

    // 엔터 키 동작
    const handleKeyPress = (e) => {
        if (e.key === "Enter") {
            fetchData();
        }
    };

    // 버튼 클릭 동작
    const handleButtonClick = (metric) => {
        setCurrentMetric(metric);
        if (metric === "시가총액") {
            fetchTopStocksByMarketCap();
        } else if (metric === "매출액") {
            fetchTopStocksByRevenue(selectedYear);
        } else {
            // 다른 메트릭에 대한 처리 (필요 시 추가)
            // 예: axios.get(`http://127.0.0.1:8000/top-${metric.toLowerCase()}`)
            setError(`"${metric}" 기능은 아직 구현되지 않았습니다.`);
            setStocks([]);
        }
    };

    // 외부 클릭 이벤트 감지
    useEffect(() => {
        const handleOutsideClick = (e) => {
            if (dropdownRef.current && !dropdownRef.current.contains(e.target)) {
                setIsDropdownOpen(false);
            }
        };
        document.addEventListener("mousedown", handleOutsideClick);
        return () => {
            document.removeEventListener("mousedown", handleOutsideClick);
        };
    }, []);

    // 연도 변경 시 현재 선택된 메트릭에 따라 데이터 재요청
    useEffect(() => {
        if (currentMetric === "매출액") {
            fetchTopStocksByRevenue(selectedYear);
        }
        // 시가총액에도 연도 적용이 필요하다면 여기에 추가
    }, [selectedYear]);

    return (
        <div
            style={{
                display: "flex",
                flexDirection: "column",
                justifyContent: "flex-start",
                alignItems: "center",
                height: "100vh",
                paddingTop: "50px",
                fontFamily: "Arial, sans-serif",
                backgroundColor: "#f9f9f9",
            }}
        >
            <h1
                onClick={resetState}
                style={{
                    cursor: "pointer",
                    color: "#007bff",
                    textAlign: "center",
                    marginBottom: "20px",
                }}
            >
                Stockter
            </h1>

            {/* 메트릭 버튼들과 연도 선택 드롭다운 */}
            <div
                style={{
                    display: "flex",
                    flexWrap: "wrap",
                    gap: "10px",
                    width: "80%",
                    marginBottom: "20px",
                    justifyContent: "center",
                    alignItems: "center",
                }}
            >
                {/* 연도 선택 드롭다운 - 왼쪽에 위치 */}
                <div style={{ display: "flex", alignItems: "center", gap: "5px" }}>
                    <label htmlFor="year-select" style={{ fontSize: "14px", fontWeight: "bold" }}>
                        연도 선택:
                    </label>
                    <select
                        id="year-select"
                        value={selectedYear}
                        onChange={(e) => setSelectedYear(e.target.value)}
                        style={{
                            padding: "10px",
                            fontSize: "14px",
                            border: "1px solid #ccc",
                            borderRadius: "4px",
                            cursor: "pointer",
                            minWidth: "100px",
                        }}
                    >
                        <option value="2021">2021</option>
                        <option value="2022">2022</option>
                        <option value="2023">2023</option>
                        <option value="2024">2024</option>
                    </select>
                </div>

                {/* 시가총액 버튼 */}
                <button
                    onClick={() => handleButtonClick("시가총액")}
                    style={{
                        padding: "10px",
                        fontSize: "14px",
                        background: "#f0f0f0",
                        border: "1px solid #ccc",
                        borderRadius: "4px",
                        cursor: "pointer",
                        minWidth: "120px",
                        textAlign: "center",
                    }}
                >
                    시가총액
                </button>

                {/* 매출액 버튼 */}
                <button
                    onClick={() => handleButtonClick("매출액")}
                    style={{
                        padding: "10px",
                        fontSize: "14px",
                        background: "#f0f0f0",
                        border: "1px solid #ccc",
                        borderRadius: "4px",
                        cursor: "pointer",
                        minWidth: "120px",
                        textAlign: "center",
                    }}
                >
                    매출액
                </button>

                {/* 나머지 메트릭 버튼 */}
                {["영업이익률", "순이익률", "EPS", "PER", "PBR", "시가배당률(%)"].map((metric) => (
                    <button
                        key={metric}
                        onClick={() => handleButtonClick(metric)}
                        style={{
                            padding: "10px",
                            fontSize: "14px",
                            background: "#f0f0f0",
                            border: "1px solid #ccc",
                            borderRadius: "4px",
                            cursor: "pointer",
                            minWidth: "120px",
                            textAlign: "center",
                        }}
                    >
                        {metric}
                    </button>
                ))}
            </div>

            {/* 검색창 */}
            <div
                style={{
                    marginBottom: isDropdownOpen ? `${Math.min(filteredStocks.length * 40, 200) + 40}px` : "20px",
                    position: "relative",
                    display: "flex",
                    alignItems: "center",
                    justifyContent: "center",
                    width: "600px",
                }}
            >
                <input
                    type="text"
                    placeholder="종목명 또는 종목코드 입력"
                    value={query}
                    onChange={handleQueryChange}
                    onKeyPress={handleKeyPress}
                    style={{
                        flex: 1,
                        padding: "10px",
                        fontSize: "16px",
                        border: "1px solid #ccc",
                        borderRadius: "4px 0 0 4px",
                    }}
                />
                <button
                    onClick={fetchData}
                    style={{
                        padding: "10px 20px",
                        fontSize: "16px",
                        background: "#007bff",
                        color: "#fff",
                        border: "none",
                        borderRadius: "0 4px 4px 0",
                        cursor: "pointer",
                        minWidth: "120px",
                    }}
                >
                    종목 검색
                </button>
                {isDropdownOpen && (
                    <div
                        ref={dropdownRef}
                        style={{
                            position: "absolute",
                            top: "50px",
                            width: "100%",
                            maxWidth: "600px",
                            background: "#fff",
                            border: "1px solid #ccc",
                            zIndex: 1000,
                            maxHeight: "200px",
                            overflowY: "auto",
                        }}
                    >
                        {filteredStocks.map((stock, index) => (
                            <div
                                key={index}
                                onMouseEnter={(e) => (e.currentTarget.style.backgroundColor = "#e6f7ff")}
                                onMouseLeave={(e) => (e.currentTarget.style.backgroundColor = "white")}
                                onClick={() => handleSuggestionClick(stock)}
                                style={{
                                    padding: "8px",
                                    cursor: "pointer",
                                    borderBottom: "1px solid #eee",
                                    transition: "background-color 0.2s ease",
                                    display: "flex",
                                    justifyContent: "space-between",
                                }}
                            >
                                <span
                                    dangerouslySetInnerHTML={{
                                        __html: highlightMatch(stock["종목명"], query),
                                    }}
                                ></span>
                                <span
                                    dangerouslySetInnerHTML={{
                                        __html: highlightMatch(stock["종목코드"].toString(), query),
                                    }}
                                    style={{ color: "#666", fontSize: "14px" }}
                                ></span>
                            </div>
                        ))}
                    </div>
                )}
            </div>

            {error && <p style={{ color: "red", marginBottom: "20px" }}>{error}</p>}

            {/* 데이터 테이블 */}
            {stocks.length > 0 && (
                <div
                    style={{
                        overflowX: "auto",
                        marginTop: "20px",
                        textAlign: "left",
                        width: "100%",
                        display: "flex",
                        justifyContent: "center",
                    }}
                >
                    <table
                        border="1"
                        style={{
                            width: "max-content",
                            borderCollapse: "collapse",
                            backgroundColor: "#fff",
                        }}
                    >
                        <thead>
                            <tr>
                                {Object.keys(stocks[0]).map((key, index) => (
                                    <th
                                        key={index}
                                        style={{
                                            padding: "8px",
                                            background: "#f2f2f2",
                                            textAlign: "center",
                                        }}
                                    >
                                        {key}
                                    </th>
                                ))}
                            </tr>
                        </thead>
                        <tbody>
                            {stocks.map((stock, index) => (
                                <tr key={index}>
                                    {Object.entries(stock).map(([key, value], idx) => {
                                        // '시가총액'과 '매출액' 관련 컬럼은 포맷팅
                                        if (key === "시가총액" || key === "매출액") {
                                            return (
                                                <td
                                                    key={idx}
                                                    style={{
                                                        padding: "8px",
                                                        textAlign: "center",
                                                    }}
                                                >
                                                    {formatNumber(key, value)}
                                                </td>
                                            );
                                        } else {
                                            return (
                                                <td
                                                    key={idx}
                                                    style={{
                                                        padding: "8px",
                                                        textAlign: "center",
                                                    }}
                                                >
                                                    {value}
                                                </td>
                                            );
                                        }
                                    })}
                                </tr>
                            ))}
                        </tbody>
                    </table>
                </div>
            )}
        </div>
    );

};

export default App;
