import React, { useState, useEffect } from "react";
import axios from "axios";
import SimpleTable from "./SimpleTable";
import "../styles/Common.css";
import "../styles/FinancialStatementsPage.css";

const FinancialStatementsPage = () => {
    const [stocks, setStocks] = useState([]);
    const [error, setError] = useState("");
    const [selectedYear, setSelectedYear] = useState("2024");
    const [currentMetric, setCurrentMetric] = useState(null);

    // 데이터 가져오기
    const fetchTopStocksByMetric = () => {
        if (currentMetric === "시가총액") {
            axios
                .get("http://127.0.0.1:8000/top-marketcap")
                .then((response) => {
                    if (response.data.error || response.data.stocks.length === 0) {
                        setError("시가총액 데이터가 없습니다.");
                        setStocks([]);
                    } else {
                        setStocks(response.data.stocks);
                        setError("");
                    }
                })
                .catch((error) => {
                    console.error("데이터 로드 중 오류 발생:", error);
                    setError("서버와 연결할 수 없습니다.");
                    setStocks([]);
                });
        } else if (currentMetric === "매출액") {
            axios
                .get("http://127.0.0.1:8000/top-revenue", { params: { year: selectedYear } })
                .then((response) => {
                    if (response.data.error || response.data.stocks.length === 0) {
                        setError("매출액 데이터가 없습니다.");
                        setStocks([]);
                    } else {
                        setStocks(
                            response.data.stocks.map((stock) => ({
                                ...stock,
                                데이터: stock["매출액"], // 매출액을 데이터로 매핑
                            }))
                        );
                        setError("");
                    }
                })
                .catch((error) => {
                    console.error("데이터 로드 중 오류 발생:", error);
                    setError("서버와 연결할 수 없습니다.");
                    setStocks([]);
                });
        } else {
            setError(`"${currentMetric}" 기능은 아직 구현되지 않았습니다.`);
            setStocks([]);
        }
    };

    // 버튼 클릭 동작
    const handleButtonClick = (metric) => {
        setCurrentMetric(metric);
    };

    // 연도 변경 시 현재 선택된 메트릭에 따라 데이터 재요청
    useEffect(() => {
        if (currentMetric) {
            fetchTopStocksByMetric();
        }
    }, [selectedYear, currentMetric]);

    return (
        <div className="financial-statements-container">
            <h2 style={{ color: "#007bff", marginBottom: "20px" }}>재무제표</h2>

            {/* 메트릭 버튼들과 연도 선택 드롭다운 */}
            <div style={{ display: "flex", flexWrap: "wrap", gap: "10px", marginBottom: "20px" }}>
                {/* 연도 선택 드롭다운 */}
                <div style={{ display: "flex", alignItems: "center", gap: "5px" }}>
                    <label htmlFor="year-select" style={{ fontSize: "14px", fontWeight: "bold" }}>
                        연도 선택:
                    </label>
                    <select
                        id="year-select"
                        value={selectedYear}
                        onChange={(e) => setSelectedYear(e.target.value)}
                    >
                        <option value="2021">2021</option>
                        <option value="2022">2022</option>
                        <option value="2023">2023</option>
                        <option value="2024">2024</option>
                    </select>
                </div>

                {/* 메트릭 버튼 */}
                <button onClick={() => handleButtonClick("시가총액")}>시가총액</button>
                <button onClick={() => handleButtonClick("매출액")}>매출액</button>
            </div>

            {error && <p className="error-message">{error}</p>}

            {/* 데이터 테이블 */}
            {stocks.length > 0 && (
                <SimpleTable
                    stocks={stocks}
                    columnTitle={currentMetric || "데이터"} // 열 제목 동적 변경
                />
            )}
        </div>
    );
};

export default FinancialStatementsPage;
