            labels: financialData.map(item => item.연도 || item.분기 || item.label || "N/A"),
            datasets: [{
              // 영업이익률, 부채비율은 %로 표시, 그 외 억 단위
              label: isPercentage(selectedButton) ? `${selectedButton} (%)` : `${selectedButton} (억)`,
              data: financialData.map(item => {
