import React, { useState, useEffect, useRef, useCallback } from "react";
import axios from "axios";
import StockTable from "./StockTable";
import "../styles/Common.css";
import "../styles/SearchPage.css";
import { Bar } from "react-chartjs-2";
import "chart.js/auto"; // 자동 등록

const SearchPage = () => {
    const [stocks, setStocks] = useState([]);
    const [query, setQuery] = useState("");
    const [highlightedStock, setHighlightedStock] = useState(null); // 상단 정보용 상태
    const [filteredStocks, setFilteredStocks] = useState([]);
    const [error, setError] = useState("");
    const [isDropdownOpen, setIsDropdownOpen] = useState(false);
    const dropdownRef = useRef(null);

    // 재무 데이터 상태 (매출액, 영업이익, 순이익 등)
    const [financialData, setFinancialData] = useState(null);
    const [loadingFinancialData, setLoadingFinancialData] = useState(false);
    const [financialError, setFinancialError] = useState("");
    const [selectedButton, setSelectedButton] = useState("매출액"); // 현재 선택된 버튼 상태

    // 차트 컨테이너에 대한 참조 생성
    const chartRef = useRef(null);

    // 공백/쉼표 제거
    const cleanValue = (value) => {
        if (typeof value === "string") {
            return value.replace(/[\s,]+/g, "");
        }
        if (value === null || value === undefined) {
            return "N/A";
        }
        return value;
    };

    // 퍼센트 단위인지 확인하는 함수 (영업이익률, 부채비율)
    const isPercentage = (buttonName) => {
        const percentageButtons = ["영업이익률", "부채비율"];
        return percentageButtons.includes(buttonName);
    };

    // ---------------------------
    // 1) 검색 함수
    // ---------------------------
    const fetchData = useCallback(
        (searchQuery = query) => {
            if (!searchQuery.trim()) {
                setError("검색어를 입력하세요.");
                return;
            }
            setError("");
            setFilteredStocks([]);
            setIsDropdownOpen(false);

            axios
                .get("https://port-0-stockter-back-m5or7nt39f4a0f5c.sel4.cloudtype.app/data", {
                    params: { query: searchQuery },
                })
                .then((response) => {
                    if (response.data.error || response.data.stocks.length === 0) {
                        setError("검색 결과가 없습니다.");
                        setStocks([]);
                    } else {
                        const mappedData = response.data.stocks.map((stock) => ({
                            ...stock,
                            매출액: cleanValue(stock["매출액"]),
                            영업이익: cleanValue(stock["영업이익"]),
                            영업이익률: cleanValue(stock["영업이익률"]),
                            시가배당률: cleanValue(stock["시가배당률(%)"]),
                            부채비율: cleanValue(stock["부채비율"]),
                        }));

                        setStocks(mappedData);
                        setHighlightedStock(mappedData[0] || null);
                    }
                })
                .catch((error) => {
                    console.error("데이터 로드 오류:", error);
                    setError("서버와 연결할 수 없습니다.");
                });
        },
        [query]
    );

    // ---------------------------
    // 2) 자동완성 로직
    // ---------------------------
    const handleQueryChange = useCallback((e) => {
        const input = e.target.value;
        setQuery(input);

        if (input.trim() === "") {
            setFilteredStocks([]);
            setIsDropdownOpen(false);
        } else {
            axios
                .get("https://port-0-stockter-back-m5or7nt39f4a0f5c.sel4.cloudtype.app/data", {
                    params: { query: input },
                })
                .then((response) => {
                    if (response.data.stocks) {
                        const lowercasedInput = input.toLowerCase();
                        const filtered = response.data.stocks.filter((stock) => {
                            const stockCode = stock["종목코드"].toString().toLowerCase();
                            const stockName = stock["종목명"].toLowerCase();
                            return (
                                stockCode.includes(lowercasedInput) ||
                                stockName.includes(lowercasedInput)
                            );
                        });

                        // 입력값으로 시작하는 종목 우선 정렬
                        const startsWithQuery = filtered.filter((stock) => {
                            const stockCode = stock["종목코드"].toString().toLowerCase();
                            const stockName = stock["종목명"].toLowerCase();
                            return (
                                stockCode.startsWith(lowercasedInput) ||
                                stockName.startsWith(lowercasedInput)
                            );
                        });
                        const others = filtered.filter((stock) => {
                            const stockCode = stock["종목코드"].toString().toLowerCase();
                            const stockName = stock["종목명"].toLowerCase();
                            return (
                                !stockCode.startsWith(lowercasedInput) &&
                                !stockName.startsWith(lowercasedInput)
                            );
                        });

                        // 최대 10개까지만
                        setFilteredStocks([...startsWithQuery, ...others].slice(0, 10));
                        setIsDropdownOpen(true);
                    }
                })
                .catch((error) => {
                    console.error("자동완성 데이터 로드 오류:", error);
                });
        }
    }, []);

    const handleSuggestionClick = useCallback(
        (stock) => {
            setQuery(stock["종목명"]);
            setIsDropdownOpen(false);
            setHighlightedStock(stock);
            fetchData(stock["종목명"]);
        },
        [fetchData]
    );

    const handleKeyPress = useCallback(
        (e) => {
            if (e.key === "Enter") {
                fetchData();
                setIsDropdownOpen(false);
            }
        },
        [fetchData]
    );

    // ---------------------------
    // 3) 기타 이벤트
    // ---------------------------
    const resetState = useCallback(() => {
        setQuery("");
        setStocks([]);
        setFilteredStocks([]);
        setError("");
        setIsDropdownOpen(false);
        setHighlightedStock(null);
        setFinancialData(null);
        setFinancialError("");
    }, []);

    useEffect(() => {
        const handleOutsideClick = (e) => {
            if (dropdownRef.current && !dropdownRef.current.contains(e.target)) {
                setIsDropdownOpen(false);
            }
        };
        document.addEventListener("mousedown", handleOutsideClick);
        return () => {
            document.removeEventListener("mousedown", handleOutsideClick);
        };
    }, []);

    const highlightMatch = useCallback((text, query) => {
        if (!query) return text;
        const regex = new RegExp(`(${query})`, "gi");
        return text.replace(regex, (match) => `<span class="highlight">${match}</span>`);
    }, []);

    // ---------------------------
    // 4) 연도별 데이터 버튼 핸들러
    // ---------------------------
    const handleButtonClick = useCallback(
        async (buttonName) => {
            if (loadingFinancialData) {
                console.warn(`${buttonName} 데이터 로드 중입니다. 잠시만 기다려주세요.`);
                return;
            }

            console.log(`${buttonName} 버튼이 클릭되었습니다.`);

            let endpoint = "";
            if (buttonName === "매출액") {
                endpoint = "financial-annual-sales";
            } else if (buttonName === "영업이익") {
                endpoint = "financial-operating-profit";
            } else if (buttonName === "영업이익률") {
                endpoint = "financial-operating-income-rate"; 
            } else if (buttonName === "순이익") {
                endpoint = "financial-net-income";
            } else if (buttonName === "부채비율") {
                endpoint = "financial-debt-ratio"; 
            } else {
                console.warn("지원되지 않는 버튼입니다.");
                return;
            }

            if (!highlightedStock) {
                setFinancialError("종목을 선택하지 않았습니다. 검색 후 선택해주세요.");
                return;
            }

            setLoadingFinancialData(true);
            setFinancialError("");
            setFinancialData(null);
            setSelectedButton(buttonName); // 현재 버튼 상태 저장

            try {
                const response = await axios.get(
                    `https://port-0-stockter-back-m5or7nt39f4a0f5c.sel4.cloudtype.app/${endpoint}`,
                    { params: { stock_name: highlightedStock["종목명"] } }
                );
                console.log(`${buttonName} API 응답 데이터:`, response.data);

                let data = null;
                // 기존처럼 각각 분기 처리
                if (buttonName === "매출액" && response.data.annual_sales) {
                    data = response.data.annual_sales;
                } else if (buttonName === "영업이익" && response.data.operating_profit) {
                    data = response.data.operating_profit;
                } else if (buttonName === "영업이익률" && response.data.operating_income_rate) {
                    data = response.data.operating_income_rate;
                } else if (buttonName === "순이익" && response.data.net_income) {
                    data = response.data.net_income;
                } else if (buttonName === "부채비율" && response.data.debt_ratio) {
                    data = response.data.debt_ratio;
                }

                if (data) {
                    setFinancialData(data);
                } else {
                    setFinancialError(`${buttonName} 데이터가 없습니다.`);
                }
            } catch (error) {
                console.error(`${buttonName} 데이터 로드 중 오류 발생:`, error);
                setFinancialError(`${buttonName} 데이터를 가져오는 중 오류가 발생했습니다.`);
            } finally {
                setLoadingFinancialData(false);
            }
        },
        [highlightedStock, loadingFinancialData]
    );

    // ---------------------------
    // 4-1) "분기별 데이터" 버튼 핸들러
    // ---------------------------
    const handleQuarterlyDataClick = useCallback(async () => {
        // 백엔드에 "분기별 데이터"를 요청한다고 가정
        // (실제 엔드포인트: "financial-quarterly-data")
        const buttonName = "분기별 데이터";

        if (loadingFinancialData) {
            console.warn(`${buttonName} 로드 중입니다. 잠시만 기다려주세요.`);
            return;
        }
        if (!highlightedStock) {
            setFinancialError("종목을 선택하지 않았습니다. 검색 후 선택해주세요.");
            return;
        }

        setLoadingFinancialData(true);
        setFinancialError("");
        setFinancialData(null);
        setSelectedButton(buttonName); // 버튼 상태 저장

        try {
            const response = await axios.get(
                `https://port-0-stockter-back-m5or7nt39f4a0f5c.sel4.cloudtype.app/financial-quarterly-data`,
                { params: { stock_name: highlightedStock["종목명"] } }
            );
            console.log(`${buttonName} API 응답 데이터:`, response.data);

            // 예: 서버에서 quarterly_data 라는 필드로 넘어온다고 가정
            let data = response.data.quarterly_data || null;
            if (data && data.length > 0) {
                setFinancialData(data);
            } else {
                setFinancialError(`분기별 데이터가 없습니다.`);
            }
        } catch (error) {
            console.error(`${buttonName} 로드 중 오류 발생:`, error);
            setFinancialError(`분기별 데이터를 가져오는 중 오류가 발생했습니다.`);
        } finally {
            setLoadingFinancialData(false);
        }
    }, [highlightedStock, loadingFinancialData]);

    // ---------------------------
    // 5) 자동 스크롤 useEffect
    // ---------------------------
    useEffect(() => {
        if (financialData && financialData.length > 0 && chartRef.current) {
            chartRef.current.scrollIntoView({ behavior: "smooth", block: "start" });
        }
    }, [financialData]);

    // ---------------------------
    // 6) JSX 반환
    // ---------------------------
    return (
        <div className="search-page-container">
            <h2
                onClick={resetState}
                style={{ cursor: "pointer", color: "#007bff", marginBottom: "20px" }}
            >
                종목검색
            </h2>

            {/* 검색창 */}
            <div style={{ position: "relative", width: "100%", maxWidth: "600px", marginBottom: "20px" }}>
                <div style={{ display: "flex" }}>
                    <input
                        type="text"
                        placeholder="종목명 또는 종목코드 입력"
                        value={query}
                        onChange={handleQueryChange}
                        onKeyPress={handleKeyPress}
                    />
                    <button className="search-button" onClick={() => fetchData()}>
                        종목 검색
                    </button>
                </div>
                {isDropdownOpen && (
                    <div ref={dropdownRef} className="dropdown">
                        {filteredStocks.map((stock, index) => (
                            <div
                                key={index}
                                className="dropdown-item"
                                onClick={() => handleSuggestionClick(stock)}
                            >
                                <span
                                    dangerouslySetInnerHTML={{
                                        __html: highlightMatch(stock["종목명"] || "", query),
                                    }}
                                ></span>
                                <span style={{ marginLeft: "10px", color: "gray" }}>
                                    {stock["종목코드"] || ""}
                                </span>
                            </div>
                        ))}
                    </div>
                )}
            </div>

            {error && <p className="error-message">{error}</p>}

            {/* 상단 종목 정보 */}
            {highlightedStock && (
                <div className="highlighted-stock">
                    <h3>종목 정보</h3>
                    <p>시장: {highlightedStock["시장"] || "N/A"}</p>
                    <p>종목코드: {highlightedStock["종목코드"] || "N/A"}</p>
                    <p>종목명: {highlightedStock["종목명"] || "N/A"}</p>
                    <p>업종명: {highlightedStock["업종명"] || "N/A"}</p>
                    <p>시가총액: {highlightedStock["시가총액"] || "N/A"}</p>
                </div>
            )}

            {/* 데이터 테이블 (기본 주가 정보) */}
            {stocks.length > 0 && (
                <StockTable
                    stocks={stocks}
                    excludeKeys={[
                        "시장",
                        "종목코드",
                        "종목명",
                        "업종명",
                        "시가총액",
                        "시가총액(숫자형)",
                        "종목명_lower",
                        "종목코드_str",
                        "부채비율", // 필요시 제외
                    ]}
                />
            )}

            {/* ========== 연간 재무 데이터 섹션 ========== */}
            {stocks.length > 0 && (
                <>
                    <h3 style={{ marginTop: "30px", marginBottom: "10px", color: "#333" }}>
                        연간 재무 데이터
                    </h3>
                    <div
                        className="sort-buttons"
                        style={{ marginBottom: "20px", display: "flex", gap: "10px", flexWrap: "wrap" }}
                    >
                        <button
                            onClick={() => handleButtonClick("매출액")}
                            disabled={loadingFinancialData && selectedButton === "매출액"}
                        >
                            {loadingFinancialData && selectedButton === "매출액"
                                ? "매출액 로딩 중..."
                                : "매출액"}
                        </button>
                        <button
                            onClick={() => handleButtonClick("영업이익")}
                            disabled={loadingFinancialData && selectedButton === "영업이익"}
                        >
                            {loadingFinancialData && selectedButton === "영업이익"
                                ? "영업이익 로딩 중..."
                                : "영업이익"}
                        </button>
                        <button
                            onClick={() => handleButtonClick("영업이익률")}
                            disabled={loadingFinancialData && selectedButton === "영업이익률"}
                        >
                            {loadingFinancialData && selectedButton === "영업이익률"
                                ? "영업이익률 로딩 중..."
                                : "영업이익률"}
                        </button>
                        <button
                            onClick={() => handleButtonClick("순이익")}
                            disabled={loadingFinancialData && selectedButton === "순이익"}
                        >
                            {loadingFinancialData && selectedButton === "순이익"
                                ? "순이익 로딩 중..."
                                : "순이익"}
                        </button>
                        <button
                            onClick={() => handleButtonClick("부채비율")}
                            disabled={loadingFinancialData && selectedButton === "부채비율"}
                        >
                            {loadingFinancialData && selectedButton === "부채비율"
                                ? "부채비율 로딩 중..."
                                : "부채비율"}
                        </button>
                    </div>
                </>
            )}

            {/* ========== 분기별 데이터 섹션 ========== */}
            {stocks.length > 0 && (
                <>
                    <h3 style={{ marginTop: "20px", marginBottom: "10px", color: "#333" }}>
                        분기별 데이터
                    </h3>
                    <div style={{ marginBottom: "20px" }}>
                        <button
                            onClick={handleQuarterlyDataClick}
                            disabled={loadingFinancialData && selectedButton === "분기별 데이터"}
                            style={{ padding: "10px 20px", cursor: "pointer" }}
                        >
                            {loadingFinancialData && selectedButton === "분기별 데이터"
                                ? "분기별 데이터 로딩 중..."
                                : "분기별 데이터"}
                        </button>
                    </div>
                </>
            )}

            {/* 데이터 로딩 및 오류 메시지 */}
            {loadingFinancialData && <p>데이터를 로드 중입니다...</p>}
            {financialError && <p className="error-message">{financialError}</p>}

            {/* 차트 표시 (financialData) */}
            {financialData && financialData.length > 0 && (
                <div className="financial-data-chart" style={{ marginTop: "20px" }}>
                    <div className="chart-container" ref={chartRef} style={{ height: "400px" }}>
                        <Bar
                            data={{
                                // 분기형 데이터에는 item.분기, 연간 데이터에는 item.연도
                                // 둘 다 가능하도록 '연도 || 분기' 식으로 처리
                                labels: financialData.map(
                                    (item) => item.연도 || item.분기 || item.label || "N/A"
                                ),
                                datasets: [
                                    {
                                        // 영업이익률, 부채비율은 %로 표시, 그 외 억 단위
                                        label: isPercentage(selectedButton)
                                            ? `${selectedButton} (%)`
                                            : `${selectedButton} (억)`,
                                        data: financialData.map((item) => {
                                            if (selectedButton === "분기별 데이터") {
                                                // 분기별 데이터를 어떤 방식으로 표시할지
                                                // 실제 응답 형태에 맞게 수정하세요 (예: item.value)
                                                return item.value;
                                            } else if (isPercentage(selectedButton)) {
                                                // 영업이익률, 부채비율
                                                return item[
                                                    selectedButton === "영업이익률"
                                                        ? "영업이익률"
                                                        : "부채비율"
                                                ];
                                            } else {
                                                // 매출액, 영업이익, 순이익 등
                                                return item[selectedButton];
                                            }
                                        }),
                                        backgroundColor:
                                            selectedButton === "매출액"
                                                ? "rgba(75, 192, 192, 0.6)"
                                                : selectedButton === "영업이익"
                                                ? "rgba(255, 159, 64, 0.6)"
                                                : selectedButton === "영업이익률"
                                                ? "rgba(54, 162, 235, 0.6)"
                                                : selectedButton === "순이익"
                                                ? "rgba(153, 102, 255, 0.6)"
                                                : selectedButton === "부채비율"
                                                ? "rgba(255, 206, 86, 0.6)"
                                                : "rgba(75, 192, 192, 0.6)",

                                        borderColor:
                                            selectedButton === "매출액"
                                                ? "rgba(75, 192, 192, 1)"
                                                : selectedButton === "영업이익"
                                                ? "rgba(255, 159, 64, 1)"
                                                : selectedButton === "영업이익률"
                                                ? "rgba(54, 162, 235, 1)"
                                                : selectedButton === "순이익"
                                                ? "rgba(153, 102, 255, 1)"
                                                : selectedButton === "부채비율"
                                                ? "rgba(255, 206, 86, 1)"
                                                : "rgba(75, 192, 192, 1)",
                                        borderWidth: 1,
                                    },
                                ],
                            }}
                            options={{
                                responsive: true,
                                maintainAspectRatio: false,
                                plugins: {
                                    legend: {
                                        position: "top",
                                    },
                                    title: {
                                        display: true,
                                        text: `${highlightedStock["종목명"]} ${
                                            selectedButton.includes("분기")
                                                ? "분기별"
                                                : "연간"
                                        } ${selectedButton}`,
                                    },
                                },
                                scales: {
                                    y: {
                                        beginAtZero: false,
                                        ticks: {
                                            callback: function (value) {
                                                // 분기별 데이터로 가져온 값도 %인지 억인지 구분해야 한다면 추가 로직 필요
                                                if (isPercentage(selectedButton)) {
                                                    return value.toFixed(1) + "%";
                                                }
                                                return value.toLocaleString() + "억";
                                            },
                                        },
                                    },
                                },
                            }}
                        />
                    </div>
                </div>
            )}
        </div>
    );
};

export default SearchPage;
