// src/App.jsx
import React from "react";
import { BrowserRouter as Router, Routes, Route, Link } from "react-router-dom";
import SearchPage from "./components/SearchPage";
import FinancialStatementsPage from "./components/FinancialStatementsPage";

const App = () => {
    return (
        <Router>
            <div
                style={{
                    display: "flex",
                    flexDirection: "column",
                    alignItems: "center",
                    fontFamily: "Arial, sans-serif",
                    backgroundColor: "#f9f9f9",
                    minHeight: "100vh",
                }}
            >
                {/* 네비게이션 바 */}
                <nav
                    style={{
                        width: "100%",
                        padding: "10px 0",
                        backgroundColor: "#007bff",
                        marginBottom: "20px",
                    }}
                >
                    <ul
                        style={{
                            listStyle: "none",
                            display: "flex",
                            justifyContent: "center",
                            gap: "20px",
                            margin: 0,
                            padding: 0,
                        }}
                    >
                        <li>
                            <Link to="/" style={{ color: "#fff", textDecoration: "none", fontSize: "18px" }}>
                                종목검색
                            </Link>
                        </li>
                        <li>
                            <Link to="/financial-statements" style={{ color: "#fff", textDecoration: "none", fontSize: "18px" }}>
                                재무제표
                            </Link>
                        </li>
                    </ul>
                </nav>

                {/* 라우트 설정 */}
                <Routes>
                    <Route path="/" element={<SearchPage />} />
                    <Route path="/financial-statements" element={<FinancialStatementsPage />} />
                </Routes>
            </div>
        </Router>
    );
};

export default App;
