import React, { useState, useEffect } from "react";

function StockTable() {
  const [stocks, setStocks] = useState([]);
  const [sortColumn, setSortColumn] = useState("2021.12 매출액");
  const [sortOrder, setSortOrder] = useState("asc");

  useEffect(() => {
    fetch(`/stocks?column_name=${sortColumn}&order=${sortOrder}`)
      .then((response) => response.json())
      .then((data) => setStocks(data));
  }, [sortColumn, sortOrder]);

  const handleSort = (column) => {
    setSortColumn(column);
    setSortOrder((prevOrder) => (prevOrder === "asc" ? "desc" : "asc"));
  };

  return (
    <div>
      <h1>Stock Table</h1>
      <table border="1">
        <thead>
          <tr>
            {["2021.12 매출액", "2022.12 매출액", "2023.12 매출액"].map((col) => (
              <th key={col} onClick={() => handleSort(col)}>
                {col} {sortColumn === col ? (sortOrder === "asc" ? "↑" : "↓") : ""}
              </th>
            ))}
          </tr>
        </thead>
        <tbody>
          {stocks.map((stock, index) => (
            <tr key={index}>
              <td>{stock["2021.12 매출액"]}</td>
              <td>{stock["2022.12 매출액"]}</td>
              <td>{stock["2023.12 매출액"]}</td>
            </tr>
          ))}
        </tbody>
      </table>
    </div>
  );
}

export default StockTable;
