import React, { useState, useEffect, useRef, useCallback } from "react";
import axios from "axios";
import debounce from "lodash.debounce";

const App = () => {
    const [stocks, setStocks] = useState([]);
    const [query, setQuery] = useState("");
    const [filteredStocks, setFilteredStocks] = useState([]);
    const [error, setError] = useState("");
    const [isDropdownOpen, setIsDropdownOpen] = useState(false);
    const [isLoading, setIsLoading] = useState(false);
    const dropdownRef = useRef(null);

    const API_URL = process.env.REACT_APP_API_URL || "http://127.0.0.1:8000";

    // 검색어 강조 표시 함수
    const highlightMatch = (text, query) => {
        if (!query) return text;
        const regex = new RegExp(`(${query})`, "gi");
        const parts = text.split(regex);
        return parts.map((part, index) =>
            regex.test(part) ? <span key={index} style={{ color: 'red' }}>{part}</span> : part
        );
    };

    // 검색 데이터 가져오기
    const fetchData = (searchQuery = query) => {
        if (!searchQuery.trim()) {
            setError("검색어를 입력하세요.");
            return;
        }
        setError("");
        setFilteredStocks([]);
        setIsDropdownOpen(false);
        setIsLoading(true);

        axios.get(`${API_URL}/data`, { params: { query: searchQuery } })
            .then((response) => {
                if (response.data.error || response.data.stocks.length === 0) {
                    setError("검색 결과가 없습니다.");
                    setStocks([]);
                } else {
                    setStocks(response.data.stocks);
                    setError("");
                }
            })
            .catch((error) => {
                console.error("데이터 로드 중 오류 발생:", error);
                setError("서버와 연결할 수 없습니다.");
                setStocks([]);
            })
            .finally(() => {
                setIsLoading(false);
            });
    };

    // 메트릭별 상위 종목 가져오기
    const fetchTopStocksByMetric = (metric) => {
        setError("");
        setStocks([]);
        setIsLoading(true);

        axios.get(`${API_URL}/top-${metric}`)
            .then((response) => {
                if (response.data.error || response.data.stocks.length === 0) {
                    setError(`${metric} 데이터가 없습니다.`);
                    setStocks([]);
                } else {
                    setStocks(response.data.stocks);
                    setError("");
                }
            })
            .catch((error) => {
                console.error("데이터 로드 중 오류 발생:", error);
                setError("서버와 연결할 수 없습니다.");
                setStocks([]);
            })
            .finally(() => {
                setIsLoading(false);
            });
    };

    // 메트릭 매핑
    const metricMap = {
        "시가총액": "marketcap",
        "매출액": "revenue",
        "영업이익률": "operating-margin",
        "순이익률": "net-margin",
        "EPS": "eps",
        "PER": "per",
        "PBR": "pbr",
        "시가배당률(%)": "dividend-yield",
    };

    // 버튼 클릭 핸들러
    const handleButtonClick = (metric) => {
        const apiMetric = metricMap[metric];
        if (apiMetric) {
            fetchTopStocksByMetric(apiMetric);
        } else {
            setError("유효하지 않은 메트릭입니다.");
        }
    };

    // 입력값 변경 핸들러 (디바운스 적용)
    const handleQueryChange = (e) => {
        const input = e.target.value;
        setQuery(input);
        if (input.trim() === "") {
            setFilteredStocks([]);
            setIsDropdownOpen(false);
            return;
        }
        debouncedFetchSuggestions(input);
    };

    // 디바운스된 자동완성 요청
    const fetchSuggestions = (input) => {
        axios.get(`${API_URL}/data`, { params: { query: input } })
            .then((response) => {
                if (response.data.stocks) {
                    const lowercasedInput = input.toLowerCase();
                    const filtered = response.data.stocks.filter((stock) => {
                        const stockCode = stock["종목코드"].toString().toLowerCase();
                        const stockName = stock["종목명"].toLowerCase();
                        return stockCode.includes(lowercasedInput) || stockName.includes(lowercasedInput);
                    });

                    const startsWithQuery = filtered.filter((stock) => {
                        const stockCode = stock["종목코드"].toString().toLowerCase();
                        const stockName = stock["종목명"].toLowerCase();
                        return stockCode.startsWith(lowercasedInput) || stockName.startsWith(lowercasedInput);
                    });

                    const others = filtered.filter((stock) => {
                        const stockCode = stock["종목코드"].toString().toLowerCase();
                        const stockName = stock["종목명"].toLowerCase();
                        return !stockCode.startsWith(lowercasedInput) && !stockName.startsWith(lowercasedInput);
                    });

                    setFilteredStocks([...startsWithQuery, ...others].slice(0, 10));
                    setIsDropdownOpen(true);
                }
            })
            .catch((error) => {
                console.error("자동완성 데이터 로드 오류:", error);
            });
    };

    const debouncedFetchSuggestions = useCallback(
        debounce((input) => {
            fetchSuggestions(input);
        }, 300),
        []
    );

    // 초기 상태로 리셋
    const resetState = () => {
        setQuery("");
        setStocks([]);
        setFilteredStocks([]);
        setError("");
        setIsDropdownOpen(false);
    };

    // 추천 클릭
    const handleSuggestionClick = (stock) => {
        setQuery(stock["종목명"]);
        setIsDropdownOpen(false);
        fetchData(stock["종목명"]);
    };

    // 엔터 키 동작
    const handleKeyPress = (e) => {
        if (e.key === "Enter") {
            fetchData();
        }
    };

    // 외부 클릭 이벤트 감지
    useEffect(() => {
        const handleOutsideClick = (e) => {
            if (dropdownRef.current && !dropdownRef.current.contains(e.target)) {
                setIsDropdownOpen(false);
            }
        };
        document.addEventListener("mousedown", handleOutsideClick);
        return () => {
            document.removeEventListener("mousedown", handleOutsideClick);
        };
    }, []);

    return (
        <div
            style={{
                display: "flex",
                flexDirection: "column",
                justifyContent: "flex-start",
                alignItems: "center",
                height: "100vh",
                paddingTop: "50px",
                fontFamily: "Arial, sans-serif",
            }}
        >
            <h1
                onClick={resetState}
                style={{
                    cursor: "pointer",
                    color: "#007bff",
                    textAlign: "center",
                }}
            >
                Stockter
            </h1>
            <div
                style={{
                    marginBottom: isDropdownOpen ? `${Math.min(filteredStocks.length * 40, 200) + 40}px` : "20px",
                    position: "relative",
                    display: "flex",
                    alignItems: "center",
                    justifyContent: "center",
                    width: "600px",
                }}
            >
                <input
                    type="text"
                    placeholder="종목명 또는 종목코드 입력"
                    value={query}
                    onChange={handleQueryChange}
                    onKeyDown={handleKeyPress}
                    style={{
                        flex: 1,
                        padding: "10px",
                        fontSize: "16px",
                        border: "1px solid #ccc",
                        borderRadius: "4px 0 0 4px",
                    }}
                />
                <button
                    onClick={() => fetchData()}
                    style={{
                        padding: "10px 20px",
                        fontSize: "16px",
                        background: "#007bff",
                        color: "#fff",
                        border: "none",
                        borderRadius: "0 4px 4px 0",
                        cursor: "pointer",
                    }}
                >
                    종목 검색
                </button>
                {isDropdownOpen && (
                    <div
                        ref={dropdownRef}
                        style={{
                            position: "absolute",
                            top: "50px",
                            width: "100%",
                            maxWidth: "600px",
                            background: "#fff",
                            border: "1px solid #ccc",
                            zIndex: 1000,
                            maxHeight: "200px",
                            overflowY: "auto",
                        }}
                    >
                        {filteredStocks.map((stock) => (
                            <div
                                key={stock["종목코드"]}
                                onMouseEnter={(e) => (e.currentTarget.style.backgroundColor = "#e6f7ff")}
                                onMouseLeave={(e) => (e.currentTarget.style.backgroundColor = "white")}
                                onClick={() => handleSuggestionClick(stock)}
                                style={{
                                    padding: "8px",
                                    cursor: "pointer",
                                    borderBottom: "1px solid #eee",
                                    transition: "background-color 0.2s ease",
                                    display: "flex",
                                    justifyContent: "space-between",
                                }}
                            >
                                <span>
                                    {highlightMatch(stock["종목명"], query)}
                                </span>
                                <span
                                    style={{ color: "#666", fontSize: "14px" }}
                                >
                                    {highlightMatch(stock["종목코드"].toString(), query)}
                                </span>
                            </div>
                        ))}
                    </div>
                )}
            </div>
            {isLoading && <p>로딩 중...</p>}
            {error && <p style={{ color: "red" }}>{error}</p>}
            <div
                style={{
                    display: "grid",
                    gridTemplateColumns: "repeat(auto-fit, minmax(150px, 1fr))",
                    gap: "10px",
                    width: "80%",
                    marginBottom: "20px",
                }}
            >
                {Object.keys(metricMap).map((metric) => (
                    <button
                        key={metric}
                        onClick={() => handleButtonClick(metric)}
                        style={{
                            padding: "10px",
                            fontSize: "14px",
                            background: "#f0f0f0",
                            border: "1px solid #ccc",
                            borderRadius: "4px",
                            cursor: "pointer",
                        }}
                    >
                        {metric}
                    </button>
                ))}
            </div>
            {stocks.length > 0 && (
                <div
                    style={{
                        overflowX: "auto",
                        marginTop: "20px",
                        textAlign: "left",
                        width: "100%",
                        display: "flex",
                        justifyContent: "center",
                    }}
                >
                    <table
                        border="1"
                        style={{
                            width: "max-content",
                            borderCollapse: "collapse",
                        }}
                    >
                        <thead>
                            <tr>
                                {Object.keys(stocks[0]).map((key) => (
                                    <th
                                        key={key}
                                        style={{
                                            padding: "8px",
                                            background: "#f2f2f2",
                                            textAlign: "center",
                                        }}
                                    >
                                        {key}
                                    </th>
                                ))}
                            </tr>
                        </thead>
                        <tbody>
                            {stocks.map((stock) => (
                                <tr key={stock["종목코드"]}>
                                    {Object.values(stock).map((value, idx) => (
                                        <td
                                            key={idx}
                                            style={{
                                                padding: "8px",
                                                textAlign: "center",
                                            }}
                                        >
                                            {value}
                                        </td>
                                    ))}
                                </tr>
                            ))}
                        </tbody>
                    </table>
                </div>
            )}
        </div>
    );
};

export default App;
