import React, { useState } from "react";
import axios from "axios";

const App = () => {
  const [stockName, setStockName] = useState(""); // 주식 이름 입력 값
  const [revenueData, setRevenueData] = useState(null); // 매출액 데이터
  const [error, setError] = useState(null); // 오류 메시지

  // Flask 서버에서 데이터 가져오기
  const fetchStockRevenue = async () => {
    try {
      setError(null);
      setRevenueData(null);

      const response = await axios.get("http://127.0.0.1:5000/stock-revenue", {
        params: { stock_name: stockName },
      });

      setRevenueData(response.data);
    } catch (err) {
      setError(
        err.response?.data?.error || "An error occurred while fetching data."
      );
    }
  };

  return (
    <div style={{ padding: "20px", fontFamily: "Arial, sans-serif" }}>
      <h1>Stock Revenue Viewer</h1>

      {/* 주식 이름 입력란 및 검색 버튼 */}
      <div style={{ marginBottom: "20px" }}>
        <label htmlFor="stockName" style={{ marginRight: "10px" }}>
          Enter Stock Name:
        </label>
        <input
          id="stockName"
          type="text"
          placeholder="Enter stock name"
          value={stockName}
          onChange={(e) => setStockName(e.target.value)}
          style={{ padding: "10px", width: "300px", marginRight: "10px" }}
        />
        <button onClick={fetchStockRevenue} style={{ padding: "10px 20px" }}>
          Search
        </button>
      </div>

      {/* 결과 출력 */}
      {revenueData && (
        <div style={{ marginTop: "20px", fontSize: "18px" }}>
          <h2>Revenue Data</h2>
          <p>
            <strong>Stock Name:</strong> {revenueData.stock_name}
          </p>
          <p>
            <strong>Revenue:</strong> {revenueData.revenue}
          </p>
        </div>
      )}

      {/* 오류 메시지 출력 */}
      {error && (
        <div style={{ color: "red", marginTop: "20px" }}>
          <strong>Error:</strong> {error}
        </div>
      )}
    </div>
  );
};

export default App;
