import React, { useEffect, useState } from 'react';
import axios from 'axios';

function App() {
  const [stocks, setStocks] = useState([]); // 초기값을 빈 배열로 설정
  const [loading, setLoading] = useState(true);

  useEffect(() => {
    const fetchStocks = async () => {
      try {
        const response = await axios.get('http://127.0.0.1:5000/all-stocks');
        console.log(typeof response.data); // 데이터 타입 확인
        console.log(response.data); // 데이터 확인
        setStocks(response.data); // 상태에 데이터 저장
        setLoading(false);
      } catch (error) {
        console.error('Error fetching all stock data:', error);
        setLoading(false);
      }
    };

    fetchStocks();
  }, []);

  return (
    <div style={{ padding: '20px' }}>
      <h1 style={{ textAlign: 'center' }}>종목 정보</h1>

      {loading ? (
        <p style={{ textAlign: 'center' }}>데이터를 불러오는 중...</p>
      ) : Array.isArray(stocks) && stocks.length > 0 ? (
        <table
          style={{
            width: '100%',
            borderCollapse: 'collapse',
            marginTop: '20px',
          }}
        >
          <thead>
            <tr>
              <th>종목코드</th>
              <th>종목명</th>
              <th>업종명</th>
              <th>시가총액</th>
            </tr>
          </thead>
          <tbody>
            {stocks.map((stock, index) => (
              <tr key={index}>
                <td>{stock['종목코드']}</td>
                <td>{stock['종목명']}</td>
                <td>{stock['업종명']}</td>
                <td>{stock['시가총액']}</td>
              </tr>
            ))}
          </tbody>
        </table>
      ) : (
        <p style={{ textAlign: 'center' }}>데이터가 없습니다.</p>
      )}
    </div>
  );
}

export default App;
