import React, { useState, useEffect, useRef, useCallback } from "react";
import axios from "axios";
import StockTable from "./StockTable";
import "../styles/Common.css";
import "../styles/SearchPage.css";
import { Bar } from "react-chartjs-2";
import "chart.js/auto"; // 자동 등록

const SearchPage = () => {
    const [stocks, setStocks] = useState([]);
    const [query, setQuery] = useState("");
    const [highlightedStock, setHighlightedStock] = useState(null); // 상단 정보용 상태
    const [filteredStocks, setFilteredStocks] = useState([]);
    const [error, setError] = useState("");
    const [isDropdownOpen, setIsDropdownOpen] = useState(false);
    const dropdownRef = useRef(null);

    // 매출액 데이터 상태
    const [annualSales, setAnnualSales] = useState(null);
    const [loadingSales, setLoadingSales] = useState(false);
    const [salesError, setSalesError] = useState("");

    const cleanValue = (value) => {
        if (typeof value === "string") {
          return value.replace(/[\s,]+/g, ""); // 공백과 쉼표 제거
        }
        if (value === null || value === undefined) {
          return "N/A"; // 빈 값 처리
        }
        return value; // 나머지는 그대로 반환
      };

    // ---------------------------
    // 1) 검색 함수
    // ---------------------------
    const fetchData = useCallback((searchQuery = query) => {
        if (!searchQuery.trim()) {
            setError("검색어를 입력하세요.");
            return;
        }
        setError("");
        setFilteredStocks([]);
        setIsDropdownOpen(false);
    
        axios
            .get("http://127.0.0.1:8000/data", { params: { query: searchQuery } })
            .then((response) => {
                if (response.data.error || response.data.stocks.length === 0) {
                    setError("검색 결과가 없습니다.");
                    setStocks([]);
                } else {
                    const mappedData = response.data.stocks.map((stock) => ({
                        ...stock,
                        매출액: cleanValue(stock["매출액"]),
                        영업이익: cleanValue(stock["영업이익"]),
                        영업이익률: cleanValue(stock["영업이익률"]),
                        시가배당률: cleanValue(stock["시가배당률(%)"]),
                    }));
    
                    setStocks(mappedData);
                    setHighlightedStock(mappedData[0] || null);
                }
            })
            .catch((error) => {
                console.error("데이터 로드 오류:", error);
                setError("서버와 연결할 수 없습니다.");
            });
    }, [query]);

    // ---------------------------
    // 2) 자동완성 로직
    // ---------------------------
    const handleQueryChange = useCallback((e) => {
        const input = e.target.value;
        setQuery(input);

        if (input.trim() === "") {
            setFilteredStocks([]);
            setIsDropdownOpen(false);
        } else {
            axios
                .get("http://127.0.0.1:8000/data", { params: { query: input } })
                .then((response) => {
                    if (response.data.stocks) {
                        const lowercasedInput = input.toLowerCase();
                        const filtered = response.data.stocks.filter((stock) => {
                            const stockCode = stock["종목코드"].toString().toLowerCase();
                            const stockName = stock["종목명"].toLowerCase();
                            return (
                                stockCode.includes(lowercasedInput) ||
                                stockName.includes(lowercasedInput)
                            );
                        });

                        // 입력값으로 시작하는 종목 우선 정렬
                        const startsWithQuery = filtered.filter((stock) => {
                            const stockCode = stock["종목코드"].toString().toLowerCase();
                            const stockName = stock["종목명"].toLowerCase();
                            return (
                                stockCode.startsWith(lowercasedInput) ||
                                stockName.startsWith(lowercasedInput)
                            );
                        });
                        const others = filtered.filter((stock) => {
                            const stockCode = stock["종목코드"].toString().toLowerCase();
                            const stockName = stock["종목명"].toLowerCase();
                            return (
                                !stockCode.startsWith(lowercasedInput) &&
                                !stockName.startsWith(lowercasedInput)
                            );
                        });

                        // 최대 10개까지만
                        setFilteredStocks([...startsWithQuery, ...others].slice(0, 10));
                        setIsDropdownOpen(true);
                    }
                })
                .catch((error) => {
                    console.error("자동완성 데이터 로드 오류:", error);
                });
        }
    }, []);

    const handleSuggestionClick = useCallback((stock) => {
        setQuery(stock["종목명"]);
        setIsDropdownOpen(false);
        setHighlightedStock(stock);
        fetchData(stock["종목명"]);
    }, [fetchData]);

    const handleKeyPress = useCallback((e) => {
        if (e.key === "Enter") {
            fetchData();
        }
    }, [fetchData]);

    // ---------------------------
    // 3) 기타 이벤트
    // ---------------------------
    const resetState = useCallback(() => {
        setQuery("");
        setStocks([]);
        setFilteredStocks([]);
        setError("");
        setIsDropdownOpen(false);
        setHighlightedStock(null);
        setAnnualSales(null);
        setSalesError("");
    }, []);

    useEffect(() => {
        const handleOutsideClick = (e) => {
            if (dropdownRef.current && !dropdownRef.current.contains(e.target)) {
                setIsDropdownOpen(false);
            }
        };
        document.addEventListener("mousedown", handleOutsideClick);
        return () => {
            document.removeEventListener("mousedown", handleOutsideClick);
        };
    }, []);

    const highlightMatch = useCallback((text, query) => {
        if (!query) return text;
        const regex = new RegExp(`(${query})`, "gi");
        return text.replace(regex, (match) => `<span class="highlight">${match}</span>`);
    }, []);

    // ---------------------------
    // 4) 매출액 등 버튼 핸들러
    // ---------------------------
    const handleButtonClick = useCallback(async (buttonName) => {
        if (loadingSales) {
            console.warn("매출액 데이터 로드 중입니다. 잠시만 기다려주세요.");
            return; // 이미 로딩 중이면 함수 종료
        }

        console.log(`${buttonName} 버튼이 클릭되었습니다.`);

        if (buttonName === '매출액') {

            if (!highlightedStock || !highlightedStock["종목코드"]) {
                setSalesError("종목을 먼저 선택하세요.");
                return;
            }

            setLoadingSales(true);
            setSalesError("");
            setAnnualSales(null);

            try {

                // /annual-sales?stock_code=XXXXX
                const response = await axios.get("http://127.0.0.1:8000/annual-sales", {
                    params: { stock_code: highlightedStock["종목코드"] },
                });
                console.log("매출액 API 응답 데이터:", response.data);
                setAnnualSales(response.data.annual_sales);
            } catch (error) {
                console.error("매출액 데이터 로드 중 오류 발생:", error);
                setSalesError("매출액 데이터를 가져오는 중 오류가 발생했습니다.");
            } finally {
                setLoadingSales(false);
            }
        }

        // 다른 버튼 로직은 추후 추가 가능
    }, [highlightedStock, loadingSales]);

    // ---------------------------
    // 5) JSX 반환
    // ---------------------------
    return (
        <div className="search-page-container">
            <h2
                onClick={resetState}
                style={{ cursor: "pointer", color: "#007bff", marginBottom: "20px" }}
            >
                종목검색
            </h2>

            {/* 검색창 */}
            <div style={{ position: "relative", width: "100%", maxWidth: "600px", marginBottom: "20px" }}>
                <div style={{ display: "flex" }}>
                    <input
                        type="text"
                        placeholder="종목명 또는 종목코드 입력"
                        value={query}
                        onChange={handleQueryChange}
                        onKeyPress={handleKeyPress}
                    />
                    <button className="search-button" onClick={() => fetchData()}>
                        종목 검색
                    </button>
                </div>
                {isDropdownOpen && (
                    <div ref={dropdownRef} className="dropdown">
                        {filteredStocks.map((stock, index) => (
                            <div
                                key={index}
                                className="dropdown-item"
                                onClick={() => handleSuggestionClick(stock)}
                            >
                                <span
                                    dangerouslySetInnerHTML={{
                                        __html: highlightMatch(stock["종목명"] || "", query),
                                    }}
                                ></span>
                                <span style={{ marginLeft: "10px", color: "gray" }}>
                                    {stock["종목코드"] || ""}
                                </span>
                            </div>
                        ))}
                    </div>
                )}
            </div>

            {error && <p className="error-message">{error}</p>}

            {/* 상단 종목 정보 */}
            {highlightedStock && (
                <div className="highlighted-stock">
                    <h3>종목 정보</h3>
                    <p>시장: {highlightedStock["시장"] || "N/A"}</p>
                    <p>종목코드: {highlightedStock["종목코드"] || "N/A"}</p>
                    <p>종목명: {highlightedStock["종목명"] || "N/A"}</p>
                    <p>업종명: {highlightedStock["업종명"] || "N/A"}</p>
                    <p>시가총액: {highlightedStock["시가총액"] || "N/A"}</p>
                </div>
            )}

            {/* 데이터 테이블 */}
            {stocks.length > 0 && (
                <StockTable
                    stocks={stocks}
                    excludeKeys={[
                        "시장",
                        "종목코드",
                        "종목명",
                        "업종명",
                        "시가총액",
                        "시가총액(숫자형)",
                        "종목명_lower",
                        "종목코드_str",
                    ]}
                />
            )}

            {/* 버튼들 */}
            {stocks.length > 0 && (
                <div
                    className="sort-buttons"
                    style={{ marginTop: "20px", display: "flex", gap: "10px", flexWrap: "wrap" }}
                >
                    <button
                        onClick={() => handleButtonClick('매출액')}
                        disabled={loadingSales}
                    >
                        {loadingSales ? "매출액 로딩 중..." : "매출액"}
                    </button>
                    <button onClick={() => handleButtonClick('영업이익')}>영업이익</button>
                    <button onClick={() => handleButtonClick('영업이익률')}>영업이익률</button>
                    <button onClick={() => handleButtonClick('순이익')}>순이익</button>
                    <button onClick={() => handleButtonClick('부채비율')}>부채비율</button>
                </div>
            )}

            {/* 매출액 데이터 표시 */}
            {loadingSales && <p>매출액 데이터를 로드 중입니다...</p>}
            {salesError && <p className="error-message">{salesError}</p>}
            {annualSales && (
                <div className="annual-sales" style={{ marginTop: "20px" }}>
                    <h3>연간 매출액 정보</h3>
                    {/* 테이블 표시 */}
                    <table>
                        <thead>
                            <tr>
                                <th>연도</th>
                                <th>매출액</th>
                            </tr>
                        </thead>
                        <tbody>
                            {annualSales.map((yearData, index) => (
                                <tr key={index}>
                                    <td>{yearData.연도}</td>
                                    {/* toLocaleString()로 3자리 콤마 표시 */}
                                    <td>{Number(yearData.매출액).toLocaleString()} 원</td>
                                </tr>
                            ))}
                        </tbody>
                    </table>
                    
                    {/* 차트 표시 */}
                    <div style={{ marginTop: "40px" }}>
                        <Bar
                            data={{
                                labels: annualSales.map(item => item.연도),
                                datasets: [
                                    {
                                        label: '매출액 (원)',
                                        data: annualSales.map(item => item.매출액),
                                        backgroundColor: 'rgba(75, 192, 192, 0.6)',
                                        borderColor: 'rgba(75, 192, 192, 1)',
                                        borderWidth: 1,
                                    },
                                ],
                            }}
                            options={{
                                responsive: true,
                                plugins: {
                                    legend: {
                                        position: 'top',
                                    },
                                    title: {
                                        display: true,
                                        text: `${highlightedStock["종목명"]} 연간 매출액`,
                                    },
                                },
                                scales: {
                                    y: {
                                        beginAtZero: true,
                                        ticks: {
                                            callback: function(value) {
                                                return value.toLocaleString() + ' 원';
                                            }
                                        }
                                    }
                                }
                            }}
                        />
                    </div>
                </div>
            )}
        </div>
    );

};

export default SearchPage;
