import React, { useState } from 'react';
import axios from 'axios';

function App() {
  const [stocks, setStocks] = useState([]);
  
  const fetchStocks = async (market) => {
    try {
      const response = await axios.get('http://localhost:5000/stocks', { params: { market } });
      setStocks(response.data);
    } catch (error) {
      console.error('Error fetching stock data:', error);
    }
  };

  return (
    <div>
      <button onClick={() => fetchStocks('코스피')}>코스피</button>
      <button onClick={() => fetchStocks('코스닥')}>코스닥</button>

      <table>
        <thead>
          <tr>
            <th>종목코드</th>
            <th>종목명</th>
            <th>업종명</th>
            <th>시가총액</th>
          </tr>
        </thead>
        <tbody>
          {stocks.map((stock, index) => (
            <tr key={index}>
              <td>{stock.종목코드}</td>
              <td>{stock.종목명}</td>
              <td>{stock.업종명}</td>
              <td>{stock.시가총액}</td>
            </tr>
          ))}
        </tbody>
      </table>
    </div>
  );
}

export default App;
