import React, { useState, useEffect, useRef } from "react";
import axios from "axios";

const App = () => {
    const [stocks, setStocks] = useState([]);
    const [query, setQuery] = useState("");
    const [filteredStocks, setFilteredStocks] = useState([]);
    const [error, setError] = useState("");
    const [isDropdownOpen, setIsDropdownOpen] = useState(false);
    const dropdownRef = useRef(null);

    const fetchData = () => {
        if (!query.trim()) {
            setError("검색어를 입력하세요.");
            return;
        }
        setError("");
        setFilteredStocks([]);
        setIsDropdownOpen(false);

        axios.get("http://127.0.0.1:8000/data", { params: { query } })
            .then((response) => {
                if (response.data.error || response.data.stocks.length === 0) {
                    setError("검색 결과가 없습니다.");
                    setStocks([]);
                } else {
                    const exactMatch = response.data.stocks.find(
                        (stock) => stock["종목명"] === query
                    );

                    if (!exactMatch) {
                        setError("검색 결과가 없습니다.");
                        setStocks([]);
                    } else {
                        setStocks(response.data.stocks);
                        setError("");
                    }
                }
            })
            .catch((error) => {
                console.error("데이터 로드 중 오류 발생:", error);
                setError("서버와 연결할 수 없습니다.");
                setStocks([]);
            });
    };

    const handleQueryChange = (e) => {
        const input = e.target.value;
        setQuery(input);

        if (input.trim() === "") {
            setFilteredStocks([]);
            setIsDropdownOpen(false);
        } else {
            axios.get("http://127.0.0.1:8000/data", { params: { query: input } })
                .then((response) => {
                    if (response.data.stocks) {
                        const lowercasedInput = input.toLowerCase();
                        const matches = response.data.stocks.filter((stock) => {
                            const stockCode = stock["종목코드"].toString().toLowerCase();
                            const stockName = stock["종목명"].toLowerCase();
                            return (
                                stockCode.includes(lowercasedInput) ||
                                stockName.includes(lowercasedInput)
                            );
                        });

                        setFilteredStocks(matches.slice(0, 10));
                        setIsDropdownOpen(matches.length > 0);
                    }
                })
                .catch((error) => {
                    console.error("자동완성 데이터 로드 오류:", error);
                });
        }
    };

    const highlightMatch = (text, query) => {
        const regex = new RegExp(`(${query})`, "gi");
        return text.replace(regex, (match) => `<span style="color: red">${match}</span>`);
    };

    const handleOutsideClick = (e) => {
        if (dropdownRef.current && !dropdownRef.current.contains(e.target)) {
            setIsDropdownOpen(false);
        }
    };

    useEffect(() => {
        document.addEventListener("mousedown", handleOutsideClick);
        return () => {
            document.removeEventListener("mousedown", handleOutsideClick);
        };
    }, []);

    return (
        <div style={{ margin: "20px", fontFamily: "Arial, sans-serif" }}>
            <h1>Stock Search</h1>
            <div
                style={{
                    marginBottom: "10px",
                    position: "relative",
                    display: "flex",
                    flexDirection: "column",
                    alignItems: "center",
                }}
            >
                <input
                    type="text"
                    placeholder="종목명 또는 종목코드 입력"
                    value={query}
                    onChange={handleQueryChange}
                    style={{
                        width: "100%",
                        maxWidth: "600px",
                        padding: "10px",
                        fontSize: "16px",
                        border: "1px solid #ccc",
                        borderRadius: "4px",
                        marginBottom: "10px",
                    }}
                />
                <button
                    onClick={fetchData}
                    style={{
                        width: "100%",
                        maxWidth: "200px",
                        padding: "10px",
                        fontSize: "16px",
                        background: "#007bff",
                        color: "#fff",
                        border: "none",
                        borderRadius: "4px",
                        cursor: "pointer",
                    }}
                >
                    검색
                </button>
                {isDropdownOpen && (
                    <div
                        ref={dropdownRef}
                        style={{
                            position: "absolute",
                            top: "60px",
                            width: "100%",
                            maxWidth: "600px",
                            background: "#fff",
                            border: "1px solid #ccc",
                            zIndex: 1000,
                            maxHeight: "200px",
                            overflowY: "auto",
                        }}
                    >
                        {filteredStocks.map((stock, index) => (
                            <div
                                key={index}
                                onMouseEnter={(e) => (e.currentTarget.style.backgroundColor = "#e6f7ff")}
                                onMouseLeave={(e) => (e.currentTarget.style.backgroundColor = "white")}
                                onClick={() => {
                                    setQuery(stock["종목명"]);
                                    setIsDropdownOpen(false);
                                }}
                                style={{
                                    padding: "8px",
                                    cursor: "pointer",
                                    borderBottom: "1px solid #eee",
                                    transition: "background-color 0.2s ease",
                                }}
                                dangerouslySetInnerHTML={{
                                    __html: highlightMatch(stock["종목명"], query),
                                }}
                            ></div>
                        ))}
                    </div>
                )}
            </div>
            {error && <p style={{ color: "red" }}>{error}</p>}
            {stocks.length > 0 && (
                <div style={{ overflowX: "auto" }}>
                    <table
                        border="1"
                        style={{
                            width: "100%",
                            maxWidth: "100%",
                            borderCollapse: "collapse",
                            marginTop: "20px",
                        }}
                    >
                        <thead>
                            <tr>
                                {Object.keys(stocks[0]).map((key, index) => (
                                    <th key={index} style={{ padding: "8px", background: "#f2f2f2" }}>{key}</th>
                                ))}
                            </tr>
                        </thead>
                        <tbody>
                            {stocks.map((stock, index) => (
                                <tr key={index}>
                                    {Object.values(stock).map((value, idx) => (
                                        <td key={idx} style={{ padding: "8px", textAlign: "center" }}>
                                            {value}
                                        </td>
                                    ))}
                                </tr>
                            ))}
                        </tbody>
                    </table>
                </div>
            )}
        </div>
    );
};

export default App;
