import React, { useState } from "react";
import axios from "axios";

const App = () => {
    const [stocks, setStocks] = useState([]);
    const [query, setQuery] = useState("");
    const [filteredStocks, setFilteredStocks] = useState([]);
    const [error, setError] = useState("");

    const fetchData = () => {
        if (!query.trim()) {
            setError("검색어를 입력하세요.");
            return;
        }
        setError(""); // 이전 오류 초기화
        // 검색어 기반 데이터 가져오기
        axios.get("http://127.0.0.1:8000/data", { params: { query } })
            .then((response) => {
                if (response.data.error) {
                    setError(response.data.error);
                } else {
                    setStocks(response.data.stocks);
                }
            })
            .catch((error) => {
                console.error("데이터 로드 중 오류 발생:", error);
                setError("서버와 연결할 수 없습니다.");
            });
    };

    const handleQueryChange = (e) => {
        const input = e.target.value;
        setQuery(input);

        // 실시간으로 필터링된 목록 생성 (임시로 보여줌)
        if (input.trim() === "") {
            setFilteredStocks([]);
        } else {
            axios.get("http://127.0.0.1:8000/data", { params: { query: input } })
                .then((response) => {
                    if (response.data.stocks) {
                        setFilteredStocks(response.data.stocks.slice(0, 5)); // 최대 5개만 보여줌
                    }
                })
                .catch((error) => {
                    console.error("자동완성 데이터 로드 오류:", error);
                });
        }
    };

    return (
        <div style={{ margin: "20px" }}>
            <h1>Stock Search</h1>
            <div style={{ marginBottom: "10px", position: "relative" }}>
                <input
                    type="text"
                    placeholder="종목명 또는 종목코드 입력"
                    value={query}
                    onChange={handleQueryChange}
                    style={{ width: "100%", padding: "8px", fontSize: "16px" }}
                />
                {filteredStocks.length > 0 && (
                    <div
                        style={{
                            position: "absolute",
                            top: "40px",
                            left: "0",
                            right: "0",
                            background: "#fff",
                            border: "1px solid #ccc",
                            zIndex: 1000,
                            maxHeight: "200px",
                            overflowY: "auto",
                        }}
                    >
                        {filteredStocks.map((stock, index) => (
                            <div
                                key={index}
                                onClick={() => {
                                    setQuery(stock["종목명"] || stock["종목코드"]);
                                    setFilteredStocks([]);
                                }}
                                style={{
                                    padding: "8px",
                                    cursor: "pointer",
                                    borderBottom: "1px solid #eee",
                                }}
                            >
                                {stock["종목명"]} ({stock["종목코드"]})
                            </div>
                        ))}
                    </div>
                )}
                <button
                    onClick={fetchData}
                    style={{
                        marginTop: "10px",
                        padding: "10px 20px",
                        fontSize: "16px",
                        width: "100%",
                    }}
                >
                    검색
                </button>
            </div>
            {error && <p style={{ color: "red" }}>{error}</p>}
            {stocks.length > 0 && (
                <table border="1" style={{ width: "100%", borderCollapse: "collapse" }}>
                    <thead>
                        <tr>
                            {Object.keys(stocks[0]).map((key, index) => (
                                <th key={index} style={{ padding: "8px", background: "#f2f2f2" }}>{key}</th>
                            ))}
                        </tr>
                    </thead>
                    <tbody>
                        {stocks.map((stock, index) => (
                            <tr key={index}>
                                {Object.values(stock).map((value, idx) => (
                                    <td key={idx} style={{ padding: "8px", textAlign: "center" }}>
                                        {value}
                                    </td>
                                ))}
                            </tr>
                        ))}
                    </tbody>
                </table>
            )}
        </div>
    );
};

export default App;
