// SearchPage.js

import React, { useState, useEffect, useRef } from "react";
import axios from "axios";
import StockTable from "./StockTable";
import "../styles/Common.css";
import "../styles/SearchPage.css";

const SearchPage = () => {
    const [stocks, setStocks] = useState([]);
    const [query, setQuery] = useState("");
    const [highlightedStock, setHighlightedStock] = useState(null); // 상단 정보용 상태
    const [filteredStocks, setFilteredStocks] = useState([]);
    const [error, setError] = useState("");
    const [isDropdownOpen, setIsDropdownOpen] = useState(false);
    const [annualSales, setAnnualSales] = useState({});
    const dropdownRef = useRef(null);

    // 검색 데이터 가져오기
    const fetchData = async (searchQuery = query) => {
        if (!searchQuery.trim()) {
            setError("검색어를 입력하세요.");
            return;
        }
        setError("");
        setFilteredStocks([]);
        setIsDropdownOpen(false);

        try {
            const response = await axios.get("http://127.0.0.1:8000/data", { params: { query: searchQuery } });
            if (response.data.error || response.data.stocks.length === 0) {
                setError("검색 결과가 없습니다.");
                setStocks([]);
                setHighlightedStock(null);
            } else {
                const rawData = response.data.stocks;

                // 데이터 매핑 및 결측값 처리
                const cleanValue = (value) => {
                    if (!value || value === "-" || value === "NaN") return "N/A";
                    return value.toString().replace(/,/g, ""); // 쉼표 제거
                };

                const mappedData = rawData.map((stock) => ({
                    ...stock,
                    eps: cleanValue(stock[`2021.12 EPS(원)`]) || cleanValue(stock[`2022.12 EPS(원)`]) || cleanValue(stock[`2023.12 EPS(원)`]) || cleanValue(stock[`2024.12 EPS(원)`]),
                    per: cleanValue(stock[`2021.12 PER(배)`]) || cleanValue(stock[`2022.12 PER(배)`]) || cleanValue(stock[`2023.12 PER(배)`]) || cleanValue(stock[`2024.12 PER(배)`]),
                    pbr: cleanValue(stock[`2021.12 PBR(배)`]) || cleanValue(stock[`2022.12 PBR(배)`]) || cleanValue(stock[`2023.12 PBR(배)`]) || cleanValue(stock[`2024.12 PBR(배)`]),
                    dividendPerShare: cleanValue(stock[`2021.12 주당배당금(원)`]) || cleanValue(stock[`2022.12 주당배당금(원)`]) || cleanValue(stock[`2023.12 주당배당금(원)`]) || cleanValue(stock[`2024.12 주당배당금(원)`]),
                    dividendYield: cleanValue(stock[`2021.12 시가배당률(%)`]) || cleanValue(stock[`2022.12 시가배당률(%)`]) || cleanValue(stock[`2023.12 시가배당률(%)`]) || cleanValue(stock[`2024.12 시가배당률(%)`]),
                }));

                setStocks(mappedData);
                setHighlightedStock(mappedData[0]); // 첫 번째 종목 설정
                setError("");

                // 연도별 매출액 데이터 가져오기
                const stockCode = mappedData[0]["종목코드"];
                const salesResponse = await axios.get("http://127.0.0.1:8000/annual-sales", { params: { query: stockCode } });
                if (salesResponse.data.error) {
                    console.error("매출액 데이터 가져오기 실패:", salesResponse.data.error);
                } else {
                    setAnnualSales(salesResponse.data.sales);
                }

                // 디버깅용 데이터 출력
                console.log("Mapped Data:", mappedData);
                console.log("Annual Sales:", salesResponse.data.sales);
            }
        } catch (error) {
            console.error("데이터 로드 중 오류 발생:", error);
            setError("서버와 연결할 수 없습니다.");
            setStocks([]);
            setHighlightedStock(null);
        }
    };

    const handleQueryChange = (e) => {
        const input = e.target.value;
        setQuery(input);

        if (input.trim() === "") {
            setFilteredStocks([]);
            setIsDropdownOpen(false);
        } else {
            axios
                .get("http://127.0.0.1:8000/data", { params: { query: input } })
                .then((response) => {
                    if (response.data.stocks) {
                        const lowercasedInput = input.toLowerCase();
                        const filtered = response.data.stocks.filter((stock) => {
                            const stockCode = stock["종목코드"].toString().toLowerCase();
                            const stockName = stock["종목명"].toLowerCase();
                            return stockCode.includes(lowercasedInput) || stockName.includes(lowercasedInput);
                        });

                        const startsWithQuery = filtered.filter((stock) => {
                            const stockCode = stock["종목코드"].toString().toLowerCase();
                            const stockName = stock["종목명"].toLowerCase();
                            return stockCode.startsWith(lowercasedInput) || stockName.startsWith(lowercasedInput);
                        });

                        const others = filtered.filter((stock) => {
                            const stockCode = stock["종목코드"].toString().toLowerCase();
                            const stockName = stock["종목명"].toLowerCase();
                            return !stockCode.startsWith(lowercasedInput) && !stockName.startsWith(lowercasedInput);
                        });

                        setFilteredStocks([...startsWithQuery, ...others].slice(0, 10));
                        setIsDropdownOpen(true);
                    }
                })
                .catch((error) => {
                    console.error("자동완성 데이터 로드 오류:", error);
                });
        }
    };

    const handleSuggestionClick = (stock) => {
        setQuery(stock["종목명"]);
        setIsDropdownOpen(false);
        fetchData(stock["종목명"]);
    };

    const handleKeyPress = (e) => {
        if (e.key === "Enter") {
            fetchData();
        }
    };

    const resetState = () => {
        setQuery("");
        setStocks([]);
        setFilteredStocks([]);
        setError("");
        setIsDropdownOpen(false);
        setHighlightedStock(null);
        setAnnualSales({});
    };

    useEffect(() => {
        const handleOutsideClick = (e) => {
            if (dropdownRef.current && !dropdownRef.current.contains(e.target)) {
                setIsDropdownOpen(false);
            }
        };
        document.addEventListener("mousedown", handleOutsideClick);
        return () => {
            document.removeEventListener("mousedown", handleOutsideClick);
        };
    }, []);

    const highlightMatch = (text, query) => {
        if (!query) return text;
        const regex = new RegExp(`(${query})`, "gi");
        return text.replace(regex, (match) => `<span class="highlight">${match}</span>`);
    };

    return (
        <div className="search-page-container">
            <h2 onClick={resetState} style={{ cursor: "pointer", color: "#007bff", marginBottom: "20px" }}>
                종목검색
            </h2>

            {/* 검색창 */}
            <div style={{ position: "relative", width: "100%", maxWidth: "600px", marginBottom: "20px" }}>
                <div style={{ display: "flex" }}>
                    <input
                        type="text"
                        placeholder="종목명 또는 종목코드 입력"
                        value={query}
                        onChange={handleQueryChange}
                        onKeyPress={handleKeyPress}
                    />
                    <button className="search-button" onClick={() => fetchData()}>
                        종목 검색
                    </button>
                </div>
                {isDropdownOpen && (
                    <div ref={dropdownRef} className="dropdown">
                        {filteredStocks.map((stock, index) => (
                            <div
                                key={index}
                                className="dropdown-item"
                                onClick={() => handleSuggestionClick(stock)}
                            >
                                <span
                                    dangerouslySetInnerHTML={{
                                        __html: highlightMatch(stock["종목명"] || "", query),
                                    }}
                                ></span>
                                <span style={{ marginLeft: "10px", color: "gray" }}>
                                    {stock["종목코드"] || ""}
                                </span>
                            </div>
                        ))}
                    </div>
                )}
            </div>

            {error && <p className="error-message">{error}</p>}

            {/* 상단 종목 정보 */}
            {highlightedStock && (
                <div className="highlighted-stock">
                    <h3>종목 정보</h3>
                    <p>시장: {highlightedStock["시장"] || "N/A"}</p>
                    <p>종목코드: {highlightedStock["종목코드"] || "N/A"}</p>
                    <p>종목명: {highlightedStock["종목명"] || "N/A"}</p>
                    <p>업종명: {highlightedStock["업종명"] || "N/A"}</p>
                    <p>시가총액: {highlightedStock["시가총액"] || "N/A"}</p>
                </div>
            )}

            {/* 연도별 매출액 정보 */}
            {annualSales && Object.keys(annualSales).length > 0 && (
                <div className="annual-sales">
                    <h3>연도별 매출액</h3>
                    <ul>
                        {Object.entries(annualSales).map(([year, sales]) => (
                            <li key={year}>
                                {year}: {sales}
                            </li>
                        ))}
                    </ul>
                </div>
            )}

            {/* 데이터 테이블 */}
            {stocks.length > 0 && (
                <StockTable
                    stocks={stocks}
                    excludeKeys={[
                        "시장",
                        "종목코드",
                        "종목명",
                        "업종명",
                        "시가총액",
                        "시가총액(숫자형)",
                        "종목명_lower",
                        "종목코드_str",
                    ]} // 제외할 키 전달
                />
            )}
        </div>
    );
};

export default SearchPage;
