import React, { useEffect, useState } from 'react';
import axios from 'axios';

function App() {
  const [stocks, setStocks] = useState([]); // 초기값 빈 배열

  useEffect(() => {
    const fetchStocks = async () => {
      try {
        const response = await axios.get('http://127.0.0.1:5000/all-stocks');
        console.log('Response Data:', response.data); // 데이터를 확인
        setStocks(Array.isArray(response.data) ? response.data : []); // 배열 여부 확인 후 저장
      } catch (error) {
        console.error('Error fetching data:', error);
      }
    };

    fetchStocks();
  }, []);

  return (
    <div style={{ padding: '20px' }}>
      <h1 style={{ textAlign: 'center' }}>종목 정보</h1>
      {stocks.length > 0 ? (
        <table style={{ width: '100%', borderCollapse: 'collapse', marginTop: '20px' }}>
          <thead>
            <tr>
              <th style={tableHeaderStyle}>종목코드</th>
              <th style={tableHeaderStyle}>종목명</th>
              <th style={tableHeaderStyle}>업종명</th>
              <th style={tableHeaderStyle}>시가총액</th>
            </tr>
          </thead>
          <tbody>
            {stocks.map((stock, index) => (
              <tr key={index}>
                <td style={tableDataStyle}>{stock['종목코드']}</td>
                <td style={tableDataStyle}>{stock['종목명']}</td>
                <td style={tableDataStyle}>{stock['업종명']}</td>
                <td style={tableDataStyle}>{stock['시가총액']}</td>
              </tr>
            ))}
          </tbody>
        </table>
      ) : (
        <p style={{ textAlign: 'center' }}>데이터가 없습니다.</p>
      )}
    </div>
  );
}

const tableHeaderStyle = {
  backgroundColor: '#f4f4f4',
  padding: '10px',
  border: '1px solid #ddd',
  textAlign: 'center',
};

const tableDataStyle = {
  padding: '10px',
  border: '1px solid #ddd',
  textAlign: 'center',
};

export default App;
