import React from "react";
import "../styles/Common.css";
import "../styles/StockTable.css";

const StockTable = ({ stocks, excludeKeys = [] }) => {
    if (stocks.length === 0) {
        return <p className="error-message">표시할 데이터가 없습니다.</p>;
    }

    // 연도 및 항목 키 설정
    const years = ["2021", "2022", "2023", "2024"];
    const financialKeys = [
        "매출액",
        "영업이익",
        "영업이익률",
        "순이익률",
        "EPS",
        "PER",
        "PBR",
        "주당배당금",
        "시가배당률",
    ];

    // 데이터 변환: 연도별 데이터를 매핑
    const transformedData = years.map((year) => {
        const yearData = { year }; // 연도를 포함한 데이터 객체 생성

        financialKeys.forEach((key) => {
            const columnName = `${year}.12 ${key}`;
            yearData[key] = stocks[0][columnName] || "N/A"; // 데이터 없으면 "N/A"
        });

        return yearData;
    });

    return (
        <div className="table-container">
            <table>
                <thead>
                    <tr>
                        <th>연도</th>
                        {financialKeys.map((key, index) => (
                            <th key={index}>{key}</th>
                        ))}
                    </tr>
                </thead>
                <tbody>
                    {transformedData.map((row, index) => (
                        <tr key={index}>
                            <td>{row.year}</td>
                            {financialKeys.map((key, idx) => (
                                <td key={idx}>{row[key]}</td>
                            ))}
                        </tr>
                    ))}
                </tbody>
            </table>
        </div>
    );
};

export default StockTable;
