import React, { useState, useEffect } from "react";
import axios from "axios";
import SimpleTable from "./SimpleTable";
import "../styles/Common.css";
import "../styles/FinancialStatementsPage.css";

const FinancialStatementsPage = () => {
    const [stocks, setStocks] = useState([]);
    const [error, setError] = useState("");
    const [selectedYear, setSelectedYear] = useState(null);
    const [currentMetric, setCurrentMetric] = useState(null);
    const [showYearButtons, setShowYearButtons] = useState(false); // 연도 버튼 표시 여부
    const [perDirection, setPerDirection] = useState("desc"); // PER 방향 상태: 'asc' 또는 'desc'
    const [pbrDirection, setPbrDirection] = useState("desc"); // PBR 방향 상태: 'asc' 또는 'desc'
    const [openSubmenu, setOpenSubmenu] = useState(null); // 열려 있는 서브메뉴

    // 데이터 가져오기
    const fetchTopStocksByMetric = () => {
        if (currentMetric === "시가총액") {
            axios
                .get("http://127.0.0.1:8000/top-marketcap")
                .then((response) => {
                    if (response.data.error || response.data.stocks.length === 0) {
                        setError("시가총액 데이터가 없습니다.");
                        setStocks([]);
                    } else {
                        setStocks(response.data.stocks);
                        setError("");
                    }
                })
                .catch((error) => {
                    console.error("데이터 로드 중 오류 발생:", error);
                    setError("서버와 연결할 수 없습니다.");
                    setStocks([]);
                });
        } else if (currentMetric === "매출액") {
            axios
                .get("http://127.0.0.1:8000/top-revenue", { params: { year: selectedYear } })
                .then((response) => {
                    if (response.data.error || response.data.stocks.length === 0) {
                        setError("매출액 데이터가 없습니다.");
                        setStocks([]);
                    } else {
                        setStocks(response.data.stocks);
                        setError("");
                    }
                })
                .catch((error) => {
                    console.error("데이터 로드 중 오류 발생:", error);
                    setError("서버와 연결할 수 없습니다.");
                    setStocks([]);
                });
        } else if (currentMetric === "영업이익") {
            axios
                .get("http://127.0.0.1:8000/top-operating-income", { params: { year: selectedYear } })
                .then((response) => {
                    if (response.data.error || response.data.stocks.length === 0) {
                        setError("영업이익 데이터가 없습니다.");
                        setStocks([]);
                    } else {
                        setStocks(response.data.stocks);
                        setError("");
                    }
                })
                .catch((error) => {
                    console.error("데이터 로드 중 오류 발생:", error);
                    setError("서버와 연결할 수 없습니다.");
                    setStocks([]);
                });
        } else if (currentMetric === "영업이익률") {
            axios
                .get("http://127.0.0.1:8000/top-operating-income-rate", { params: { year: selectedYear } })
                .then((response) => {
                    if (response.data.error || response.data.stocks.length === 0) {
                        setError("영업이익률 데이터가 없습니다.");
                        setStocks([]);
                    } else {
                        setStocks(response.data.stocks);
                        setError("");
                    }
                })
                .catch((error) => {
                    console.error("데이터 로드 중 오류 발생:", error);
                    setError("서버와 연결할 수 없습니다.");
                    setStocks([]);
                });
        } else if (currentMetric === "순이익률") {
            axios
                .get("http://127.0.0.1:8000/top-net-income", { params: { year: selectedYear } })
                .then((response) => {
                    if (response.data.error || response.data.stocks.length === 0) {
                        setError("순이익률 데이터가 없습니다.");
                        setStocks([]);
                    } else {
                        setStocks(response.data.stocks);
                        setError("");
                    }
                })
                .catch((error) => {
                    console.error("데이터 로드 중 오류 발생:", error);
                    setError("서버와 연결할 수 없습니다.");
                    setStocks([]);
                });
        } else if (currentMetric === "EPS") {
            axios
                .get("http://127.0.0.1:8000/top-eps", { params: { year: selectedYear } })
                .then((response) => {
                    if (response.data.error || response.data.stocks.length === 0) {
                        setError("EPS 데이터가 없습니다.");
                        setStocks([]);
                    } else {
                        setStocks(response.data.stocks);
                        setError("");
                    }
                })
                .catch((error) => {
                    console.error("데이터 로드 중 오류 발생:", error);
                    setError("서버와 연결할 수 없습니다.");
                    setStocks([]);
                });
        } else if (currentMetric === "PER") {
            axios
                .get("http://127.0.0.1:8000/top-per", { params: { year: selectedYear } }) // 수정된 PER 엔드포인트
                .then((response) => {
                    if (response.data.error || response.data.stocks.length === 0) {
                        setError("PER 데이터가 없습니다.");
                        setStocks([]);
                    } else {
                        setStocks(response.data.stocks);
                        setError("");
                    }
                })
                .catch((error) => {
                    console.error("데이터 로드 중 오류 발생:", error);
                    setError("서버와 연결할 수 없습니다.");
                    setStocks([]);
                });
        } else if (currentMetric === "PBR") { // PBR 조건 추가
            if (pbrDirection === "desc") {
                axios
                    .get("http://127.0.0.1:8000/top-pbr", { params: { year: selectedYear } })
                    .then((response) => {
                        if (response.data.error || response.data.stocks.length === 0) {
                            setError("PBR 데이터가 없습니다.");
                            setStocks([]);
                        } else {
                            setStocks(response.data.stocks);
                            setError("");
                        }
                    })
                    .catch((error) => {
                        console.error("데이터 로드 중 오류 발생:", error);
                        setError("서버와 연결할 수 없습니다.");
                        setStocks([]);
                    });
            } else if (pbrDirection === "asc") {
                axios
                    .get("http://127.0.0.1:8000/bottom-pbr", { params: { year: selectedYear } })
                    .then((response) => {
                        if (response.data.error || response.data.stocks.length === 0) {
                            setError("PBR 데이터가 없습니다.");
                            setStocks([]);
                        } else {
                            setStocks(response.data.stocks);
                            setError("");
                        }
                    })
                    .catch((error) => {
                        console.error("데이터 로드 중 오류 발생:", error);
                        setError("서버와 연결할 수 없습니다.");
                        setStocks([]);
                    });
            }
        } else {
            setError(`${currentMetric} 기능은 아직 구현되지 않았습니다.`);
            setStocks([]);
        }
    };

    // 버튼 클릭 동작
    const handleButtonClick = (metric) => {
        if (currentMetric === metric) {
            // 이미 선택된 메트릭을 다시 클릭하면 서브메뉴 토글
            setOpenSubmenu((prev) => (prev === metric ? null : metric));
        } else {
            // 새로운 메트릭을 선택하면 서브메뉴 닫기
            setOpenSubmenu(null);
        }

        if (metric === "PER") {
            if (currentMetric === "PER") {
                // PER 방향 토글: 'desc' <-> 'asc'
                setPerDirection((prevDirection) => (prevDirection === "desc" ? "asc" : "desc"));
            } else {
                setPerDirection("desc"); // 기본 방향 설정
            }
        }

        if (metric === "PBR") {
            if (currentMetric === "PBR") {
                // PBR 방향 토글: 'desc' <-> 'asc'
                setPbrDirection((prevDirection) => (prevDirection === "desc" ? "asc" : "desc"));
            } else {
                setPbrDirection("desc"); // 기본 방향 설정
            }
        }

        setCurrentMetric(metric);
        if (metric !== "시가총액") {
            setShowYearButtons(true); // 시가총액 제외 모든 버튼 클릭 시 연도 버튼 표시
            setSelectedYear("2024"); // 기본 연도 설정
        } else {
            setShowYearButtons(false); // 시가총액 클릭 시 연도 버튼 숨기기
            setSelectedYear(null);
        }
    };

    // 연도 버튼 클릭 동작
    const handleYearButtonClick = (year) => {
        setSelectedYear(year);
    };

    // 외부 클릭 시 서브메뉴 닫기
    useEffect(() => {
        const handleClickOutside = (event) => {
            if (
                !event.target.closest('.submenu') &&
                !event.target.closest('.metric-button')
            ) {
                setOpenSubmenu(null);
            }
        };

        document.addEventListener('click', handleClickOutside);

        return () => {
            document.removeEventListener('click', handleClickOutside);
        };
    }, []);

    // 데이터 요청 트리거
    useEffect(() => {
        if (currentMetric) {
            fetchTopStocksByMetric();
        }
    }, [selectedYear, currentMetric, perDirection, pbrDirection]); // perDirection 및 pbrDirection 포함

    return (
        <div className="financial-statements-container">
            <h2 style={{ color: "#007bff", marginBottom: "20px" }}>재무제표</h2>

            {/* 정렬기준 버튼 */}
            <div style={{ display: "flex", alignItems: "center", gap: "10px", marginBottom: "10px" }}>
                {/* 시가총액 버튼 */}
                <button
                    onClick={() => handleButtonClick("시가총액")}
                    className="metric-button"
                    style={{
                        backgroundColor: currentMetric === "시가총액" ? "#007bff" : "#fff",
                        color: currentMetric === "시가총액" ? "#fff" : "#000",
                        border: "1px solid #007bff",
                        borderRadius: "4px",
                        padding: "5px 10px",
                        cursor: "pointer",
                    }}
                >
                    시가총액
                </button>

                {/* 매출액 버튼 */}
                <button
                    onClick={() => handleButtonClick("매출액")}
                    className="metric-button"
                    style={{
                        backgroundColor: currentMetric === "매출액" ? "#007bff" : "#fff",
                        color: currentMetric === "매출액" ? "#fff" : "#000",
                        border: "1px solid #007bff",
                        borderRadius: "4px",
                        padding: "5px 10px",
                        cursor: "pointer",
                    }}
                >
                    매출액
                </button>

                {/* 영업이익 버튼 */}
                <button
                    onClick={() => handleButtonClick("영업이익")}
                    className="metric-button"
                    style={{
                        backgroundColor: currentMetric === "영업이익" ? "#007bff" : "#fff",
                        color: currentMetric === "영업이익" ? "#fff" : "#000",
                        border: "1px solid #007bff",
                        borderRadius: "4px",
                        padding: "5px 10px",
                        cursor: "pointer",
                    }}
                >
                    영업이익
                </button>

                {/* 영업이익률 버튼 */}
                <button
                    onClick={() => handleButtonClick("영업이익률")}
                    className="metric-button"
                    style={{
                        backgroundColor: currentMetric === "영업이익률" ? "#007bff" : "#fff",
                        color: currentMetric === "영업이익률" ? "#fff" : "#000",
                        border: "1px solid #007bff",
                        borderRadius: "4px",
                        padding: "5px 10px",
                        cursor: "pointer",
                    }}
                >
                    영업이익률
                </button>

                {/* 순이익률 버튼 */}
                <button
                    onClick={() => handleButtonClick("순이익률")}
                    className="metric-button"
                    style={{
                        backgroundColor: currentMetric === "순이익률" ? "#007bff" : "#fff",
                        color: currentMetric === "순이익률" ? "#fff" : "#000",
                        border: "1px solid #007bff",
                        borderRadius: "4px",
                        padding: "5px 10px",
                        cursor: "pointer",
                    }}
                >
                    순이익률
                </button>

                {/* EPS 버튼 */}
                <button
                    onClick={() => handleButtonClick("EPS")}
                    className="metric-button"
                    style={{
                        backgroundColor: currentMetric === "EPS" ? "#007bff" : "#fff",
                        color: currentMetric === "EPS" ? "#fff" : "#000",
                        border: "1px solid #007bff",
                        borderRadius: "4px",
                        padding: "5px 10px",
                        cursor: "pointer",
                    }}
                >
                    EPS
                </button>

                {/* PER 버튼 */}
                <div style={{ position: "relative" }}>
                    <button
                        onClick={() => handleButtonClick("PER")}
                        className="metric-button"
                        style={{
                            backgroundColor: currentMetric === "PER" ? "#007bff" : "#fff",
                            color: currentMetric === "PER" ? "#fff" : "#000",
                            border: "1px solid #007bff",
                            borderRadius: "4px",
                            padding: "5px 10px",
                            cursor: "pointer",
                        }}
                    >
                        PER {currentMetric === "PER" ? (perDirection === "desc" ? "↑" : "↓") : ""}
                    </button>

                    {/* PER 서브메뉴 */}
                    {openSubmenu === "PER" && (
                        <div className="submenu" style={{
                            position: "absolute",
                            top: "35px",
                            left: "0",
                            backgroundColor: "#fff",
                            border: "1px solid #007bff",
                            borderRadius: "4px",
                            zIndex: 1000,
                            boxShadow: "0 2px 8px rgba(0, 0, 0, 0.15)",
                        }}>
                            <button
                                onClick={() => setPerDirection("asc")}
                                style={{
                                    display: "block",
                                    width: "100%",
                                    padding: "5px 10px",
                                    backgroundColor: perDirection === "asc" ? "#007bff" : "#fff",
                                    color: perDirection === "asc" ? "#fff" : "#000",
                                    border: "none",
                                    textAlign: "left",
                                    cursor: "pointer",
                                }}
                            >
                                오름차순
                            </button>
                            <button
                                onClick={() => setPerDirection("desc")}
                                style={{
                                    display: "block",
                                    width: "100%",
                                    padding: "5px 10px",
                                    backgroundColor: perDirection === "desc" ? "#007bff" : "#fff",
                                    color: perDirection === "desc" ? "#fff" : "#000",
                                    border: "none",
                                    textAlign: "left",
                                    cursor: "pointer",
                                }}
                            >
                                내림차순
                            </button>
                        </div>
                    )}
                </div>

                {/* PBR 버튼 */}
                <div style={{ position: "relative" }}>
                    <button
                        onClick={() => handleButtonClick("PBR")}
                        className="metric-button"
                        style={{
                            backgroundColor: currentMetric === "PBR" ? "#007bff" : "#fff",
                            color: currentMetric === "PBR" ? "#fff" : "#000",
                            border: "1px solid #007bff",
                            borderRadius: "4px",
                            padding: "5px 10px",
                            cursor: "pointer",
                        }}
                    >
                        PBR {currentMetric === "PBR" ? (pbrDirection === "desc" ? "↑" : "↓") : ""}
                    </button>

                    {/* PBR 서브메뉴 */}
                    {openSubmenu === "PBR" && (
                        <div className="submenu" style={{
                            position: "absolute",
                            top: "35px",
                            left: "0",
                            backgroundColor: "#fff",
                            border: "1px solid #007bff",
                            borderRadius: "4px",
                            zIndex: 1000,
                            boxShadow: "0 2px 8px rgba(0, 0, 0, 0.15)",
                        }}>
                            <button
                                onClick={() => setPbrDirection("asc")}
                                style={{
                                    display: "block",
                                    width: "100%",
                                    padding: "5px 10px",
                                    backgroundColor: pbrDirection === "asc" ? "#007bff" : "#fff",
                                    color: pbrDirection === "asc" ? "#fff" : "#000",
                                    border: "none",
                                    textAlign: "left",
                                    cursor: "pointer",
                                }}
                            >
                                오름차순
                            </button>
                            <button
                                onClick={() => setPbrDirection("desc")}
                                style={{
                                    display: "block",
                                    width: "100%",
                                    padding: "5px 10px",
                                    backgroundColor: pbrDirection === "desc" ? "#007bff" : "#fff",
                                    color: pbrDirection === "desc" ? "#fff" : "#000",
                                    border: "none",
                                    textAlign: "left",
                                    cursor: "pointer",
                                }}
                            >
                                내림차순
                            </button>
                        </div>
                    )}
                </div>

                {/* 시가배당률 버튼 */}
                <button
                    onClick={() => handleButtonClick("시가배당률")}
                    className="metric-button"
                    style={{
                        backgroundColor: currentMetric === "시가배당률" ? "#007bff" : "#fff",
                        color: currentMetric === "시가배당률" ? "#fff" : "#000",
                        border: "1px solid #007bff",
                        borderRadius: "4px",
                        padding: "5px 10px",
                        cursor: "pointer",
                    }}
                >
                    시가배당률
                </button>
            </div>

            {/* 연도 버튼들 (시가총액을 제외한 모든 버튼 클릭 시 표시) */}
            {showYearButtons && (
                <div style={{ display: "flex", gap: "5px", marginBottom: "20px" }}>
                    {["2021", "2022", "2023", "2024"].map((year) => (
                        <button
                            key={year}
                            onClick={() => handleYearButtonClick(year)}
                            style={{
                                backgroundColor: selectedYear === year ? "#007bff" : "#fff",
                                color: selectedYear === year ? "#fff" : "#000",
                                border: "1px solid #007bff",
                                borderRadius: "4px",
                                padding: "5px 10px",
                                cursor: "pointer",
                            }}
                        >
                            {year}
                        </button>
                    ))}
                </div>
            )}

            {error && <p className="error-message">{error}</p>}

            {/* 데이터 테이블 */}
            {stocks.length > 0 && (
                <SimpleTable
                    stocks={stocks}
                    columnTitle={currentMetric} // 동적으로 제목 변경
                />
            )}
        </div>
    );

};

export default FinancialStatementsPage;
