import React, { useEffect, useState } from 'react';
import axios from 'axios';

function App() {
  const [stocks, setStocks] = useState([]);

  // 컴포넌트가 처음 렌더링될 때 모든 종목 데이터를 가져옵니다.
  useEffect(() => {
    const fetchStocks = async () => {
      try {
        const response = await axios.get('http://localhost:5000/all-stocks');
        setStocks(response.data);
      } catch (error) {
        console.error('Error fetching all stock data:', error);
      }
    };

    fetchStocks();
  }, []);

  return (
    <div>
      <h1>종목 정보</h1>
      <table>
        <thead>
          <tr>
            <th>종목코드</th>
            <th>종목명</th>
            <th>업종명</th>
            <th>시가총액</th>
          </tr>
        </thead>
        <tbody>
          {stocks.map((stock, index) => (
            <tr key={index}>
              <td>{stock['종목코드']}</td>
              <td>{stock['종목명']}</td>
              <td>{stock['업종명']}</td>
              <td>{stock['시가총액']}</td>
            </tr>
          ))}
        </tbody>
      </table>
    </div>
  );
}

export default App;
