import React, { useEffect, useState } from 'react';
import axios from 'axios';

function App() {
  const [stocks, setStocks] = useState([]); // 주식 데이터를 저장할 상태 변수
  const [loading, setLoading] = useState(true); // 로딩 상태를 저장

  useEffect(() => {
    // Flask 서버에서 데이터 가져오기
    const fetchStocks = async () => {
      try {
        const response = await axios.get('http://localhost:5000/all-stocks'); // Flask 서버의 엔드포인트
        console.log(response.data); // 데이터 확인용 콘솔 출력
        setStocks(response.data); // 상태에 데이터 저장
        setLoading(false); // 로딩 완료
      } catch (error) {
        console.error('Error fetching all stock data:', error); // 에러 로그
        setLoading(false); // 로딩 완료 상태로 변경
      }
    };

    fetchStocks(); // 데이터 가져오기 실행
  }, []);

  return (
    <div style={{ padding: '20px' }}>
      <h1 style={{ textAlign: 'center' }}>종목 정보</h1>

      {loading ? ( // 로딩 중일 때 메시지 표시
        <p style={{ textAlign: 'center' }}>데이터를 불러오는 중...</p>
      ) : stocks.length > 0 ? ( // 데이터가 있을 경우
        <table
          style={{
            width: '100%',
            borderCollapse: 'collapse',
            marginTop: '20px',
          }}
        >
          <thead>
            <tr>
              <th style={tableHeaderStyle}>종목코드</th>
              <th style={tableHeaderStyle}>종목명</th>
              <th style={tableHeaderStyle}>업종명</th>
              <th style={tableHeaderStyle}>시가총액</th>
            </tr>
          </thead>
          <tbody>
            {stocks.map((stock, index) => (
              <tr key={index}>
                <td style={tableDataStyle}>{stock['종목코드']}</td>
                <td style={tableDataStyle}>{stock['종목명']}</td>
                <td style={tableDataStyle}>{stock['업종명']}</td>
                <td style={tableDataStyle}>{stock['시가총액']}</td>
              </tr>
            ))}
          </tbody>
        </table>
      ) : (
        <p style={{ textAlign: 'center' }}>데이터가 없습니다.</p> // 데이터가 없을 경우
      )}
    </div>
  );
}

// 테이블 스타일 정의
const tableHeaderStyle = {
  backgroundColor: '#f4f4f4',
  padding: '10px',
  border: '1px solid #ddd',
  textAlign: 'center',
};

const tableDataStyle = {
  padding: '10px',
  border: '1px solid #ddd',
  textAlign: 'center',
};

export default App;
